INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-coney-island', 'Нью-Йорк — Кони-Айленд', 'Игрок отправляется на Кони-Айленд, кружится на Cyclone, пробует хот-доги Nathan’s и погружается в летнюю ностальгию Boardwalk.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-coney-island", "title": "Нью-Йорк — Кони-Айленд", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T16:59:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T16:59:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "coney_island", "tourism"], "topics": ["lifestyle", "leisure", "nostalgia"], "related_systems": ["tourism-system", "mood-system", "economy-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-017-coney-island.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание дня на Кони-Айленд находилось в Markdown и не учитывало туристические награды и атмосферные эффекты.", "goal": "Оформить квест в YAML, связав парк развлечений Бруклина с системами туризма, настроения и экономики.", "essence": "Игрок отправляется на Кони-Айленд, кружится на Cyclone, пробует хот-доги Nathan’s и погружается в летнюю ностальгию Boardwalk.", "key_points": ["Расписаны этапы поездки, аттракционов и гастрономических остановок.", "Добавлены крючки для tourism- и mood-system, влияющие на расслабление и баффы.", "Награды закрепляют туристическую ветку и открывают события летнего сезона."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 800 XP, бафф «Летний бриз» (стресс −100%), титул «Летний день», расходы 50 едди.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Кони-Айленд на метро и войти на Boardwalk.\\n2. Прокатиться на деревянных горках Cyclone, поймать кадры старого парка.\\n3. Отправиться к Nathan’s Famous, принять участие в мини-ивенте с хот-догами.\\n4. Прогуляться вдоль набережной, посетить классические аттракционы и Freak Show.\\n5. Завершить день купанием в Атлантике и вечерним салютом на пляже.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "tourism-system начисляет очки за достопримечательности и открывает скидки летнего сезона.\\nmood-system снижает стресс и повышает ностальгию, economy-system учитывает расходы на аттракционы и еду.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны скрип деревянных горок, запах жареных хот-догов, ржавые вывески Freak Show и шум прибоя Boardwalk.\\nУказаны летние толпы, конкурсы по поеданию хот-догов и вечерние огни парка развлечений.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Награды включают 800 XP, бафф отдыха и доступ к сезонным событиям Бруклина.\\nПропуск ключевых активностей снижает эффект баффа и закрывает скидки у уличных торговцев.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Coney Island Visitor Guide", "link": "https://www.coneyisland.com"}, {"title": "History of the Cyclone Roller Coaster", "link": "https://www.nycgo.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Переведено в YAML-шаблон, добавлены системные крючки и летние награды."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание поездки на Кони-Айленд."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

