INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-wall-street-crash', 'Нью-Йорк — Крах Уолл-стрит', 'Игрок проникает в алгоритмы NYSE и решает, обрушить рынок, стабилизировать его или тихо извлечь прибыль.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-wall-street-crash", "title": "Нью-Йорк — Крах Уолл-стрит", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:06:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:06:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "wall_street", "hacking"], "topics": ["economy", "cyber-operations", "decision-making"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "hacking-service", "reputation-service", "market-system"], "related_documents": [{"id": "canon-quest-ny-lady-liberty", "relation": "follows"}, {"id": "github-issue-351", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/351", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/new-york/2020-2029/quest-002-wall-street-crash.md", "visibility": "internal", "audience": ["narrative", "systems", "economy"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Хакерский сценарий Уолл-стрит находился в Markdown и не отражал системных исходов и наград.", "goal": "Перевести квест в YAML, описав инструменты взлома биржи, экономические последствия и выбор игрока.", "essence": "Игрок проникает в алгоритмы NYSE и решает, обрушить рынок, стабилизировать его или тихо извлечь прибыль.", "key_points": ["Расписаны этапы подготовки взлома и варианты исходов с разными наградами.", "Добавлены системные крючки для economy- и hacking-system, влияющие на городскую экономику.", "Решение игрока меняет отношение корпораций, подполья и финансовых фракций."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main. Сложность: extreme. Формат: solo. Длительность: 4+ часов.\\nНаграды: до 8 000 XP, 0–500 000 едди (в зависимости от исхода), изменение состояния экономики Нью-Йорка.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить заказ на вмешательство в NYSE и собрать команду аналитиков.\\n2. Взломать торговые алгоритмы и получить контроль над ключевыми потоками капитала.\\n3. Смоделировать последствия и выбрать стратегию: крах, спасение или тихая манипуляция.\\n4. Реализовать выбранный план, управляя тревогами, медиа и действиями трейдеров.\\n5. Скрыть следы вмешательства или принять последствия публичного скандала.\\n", "mechanics_links": [], "assets": []}, {"id": "decision_points", "title": "Ключевые решения", "body": "- Крах рынка: вызвать лавину распродаж, заработать на шортах 200 000 едди, но разрушить экономику и вызвать розыск.\\n- Спасение: стабилизировать биржу, получить +10 репутации у корпораций и доступ к их контрактам.\\n- Манипуляция: скрытно обогатиться на 500 000 едди, сохранив рынок, но вызвать подозрения регуляторов.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "economy-system обновляет курсы, инфляцию и доступность товаров после вмешательства.\\nhacking-system обрабатывает мини-игры взлома, reputation-system меняет отношения корпораций, подполья и гражданских групп.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Воссозданы Trading Floor NYSE, статуя Charging Bull, паника трейдеров и прямые эфиры CNBC.\\nУказаны отсылки к краху 1929 года, лозунг «Greed is good» и корпоративные бункеры аналитиков.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Выбор игрока даёт 0–500 000 едди, до 8 000 XP и уникальные модификаторы экономики региона.\\nНебрежное вмешательство вызывает расследование SEC и блокирует вход на биржу в дальнейших квестах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "NYSE Systems Overview", "link": "https://www.nyse.com"}, {"title": "Wall Street Crashes History", "link": "https://www.history.com"}], "decisions": []}, "implementation": {"github_issue": 351, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Переведено в шаблон 1.1.0, описаны этапы и исходы квеста Уолл-стрит."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-концепт краха Уолл-стрит."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

