-- Quest: canon-quest-ny-queens-diversity
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-queens-diversity', 'Нью-Йорк — Разнообразие Квинса', 'Игрок путешествует по районам Квинса, знакомится с общинами Flushing, Jackson Heights, Astoria и Corona, ощущая «World’s Borough».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-queens-diversity", "title": "Нью-Йорк — Разнообразие Квинса", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:01:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:01:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "queens", "diversity"], "topics": ["multicultural", "gastronomy", "community"], "related_systems": ["tourism-system", "reputation-system", "mood-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-019-queens-diversity.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут по многонациональному Квинсу был в Markdown и не раскрывал влияние на репутацию культурных фракций.", "goal": "Структурировать квест в YAML, отразив гастрономические точки и социальные эффекты многообразия.", "essence": "Игрок путешествует по районам Квинса, знакомится с общинами Flushing, Jackson Heights, Astoria и Corona, ощущая «World’s Borough».", "key_points": ["Детализированы остановки в этнических кварталах с дегустациями и культурными событиями.", "Добавлены системные крючки для tourism- и reputation-system, влияющие на отношения с общинами.", "Финал подчёркивает многообразие и даёт культурные награды."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 2–4 часа.\\nНаграды: 1 200 XP, культурный бафф +15, титул «Гражданин мира», расходы 2 000 едди на дегустации.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Отправиться во Flushing, попробовать уличную китайскую кухню и познакомиться с гидом.\\n2. Посетить Jackson Heights, испытать индийские специи и латиноамериканскую музыку на улицах.\\n3. Переместиться в Astoria, зайти в греческую таверну и поговорить с местными мастерами.\\n4. Завершить маршрут в Corona, присоединиться к вечернему концерту и танцам.\\n5. Собрать отзывы жителей, сформировать карту культур Квинса и поделиться выводами.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "tourism-system начисляет очки за посещение этнических районов и открывает новые маршруты.\\nreputation-system отслеживает отношения с общинами, mood-system усиливает вдохновение и эмпатию.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны рынки с 800+ языками, вывески разных алфавитов, запахи специй и уличные фестивали.\\nПодчёркнуто прозвище «World’s Borough» и роль Квинса как точки встречи культур.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 1 200 XP, культурный бафф и скидки в этнических районах; открывается доступ к мультикультурным ивентам.\\nНевежливое поведение снижает репутацию и закрывает часть гастрономических скидок.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Queens Borough Diversity Report", "link": "https://www.nyc.gov"}, {"title": "World Cuisine Guide to Queens", "link": "https://www.timeout.com/newyork"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлены культурные крючки, системные эффекты и расширенные награды."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание маршрута по Квинсу."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

