-- Quest: canon-quest-ny-vertical-city
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-vertical-city', 'Нью-Йорк — Вертикальный город', 'Игрок обживается на 80-м этаже, лавирует между элитой и рабочими этажами и решает, кого поддержать.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-vertical-city", "title": "Нью-Йорк — Вертикальный город", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T14:54:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T14:54:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "vertical-city", "social"], "topics": ["regional-content", "social-structure", "lifestyle"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "exploration-service", "construction-service"], "related_documents": [{"id": "canon-quest-ny-flooded-manhattan", "relation": "contrasts"}, {"id": "github-issue-355", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/355", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2040-2060/quest-022-vertical-city.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Вертикальное общество Нью-Йорка описывалось в Markdown без связок с социальными механиками.", "goal": "Сформировать YAML-квест о жизни на верхних этажах, подчеркнув классовые ограничения и выбор игрока.", "essence": "Игрок обживается на 80-м этаже, лавирует между элитой и рабочими этажами и решает, кого поддержать.", "key_points": ["Расписаны маршруты через sky-bridge, расписание лифтов и влияние этажей на доступ к сервисам.", "Выделены награды социального характера и ветка конфликтов между верхами и низами.", "Заданы крючки для систем жилья, экономики услуг и репутации кланов."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: medium, формат: solo, длительность: 1-2 часа.\\nНаграды: 1,500 XP, +25 к репутации выбранного этажа, доступ к эксклюзивным услугам sky-рынка.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить временную прописку на 80-м этаже башни и изучить внутреннюю инфраструктуру.\\n2. Провести серию встреч: элита penthouse, рабочие сервисных уровней и члены советов безопасности лифтов.\\n3. Разобраться в конфликте между верхними этажами и низами из-за распределения электроэнергии.\\n4. Принять сторону: поддержать элиту, требующую изоляции, или организовать лифтовый профсоюз.\\n5. Завершить квест — получить бонусы сервиса либо открыть миссии сопротивления.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Небоскрёбы объединены sky-бриджами, формируя мини-города с торговыми аркадами и садами на высоте.\\nДети верхних уровней никогда не видели землю, социальные события происходят в sky-лобби и смотровых куполах.\\nКонтроль лифтов — ключевой ресурс, которым управляют частные охранные компании.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест влияет на систему жилья (скидки/штрафы), открывает клубы верхних этажей и миссии контрабанды лифтов.\\nВыбор игрока изменяет стоимость сервисов, открывает особые товары и определяет доступ к фракции «Skyward Syndicate».\\nКонфликт этажей синхронизируется с городскими событиями и влияет на экономику вертикальных маршрутов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Vertical Urbanism Case Studies", "link": "https://www.archdaily.com"}, {"title": "Skybridge Megacities", "link": "https://www.wired.com"}], "decisions": []}, "implementation": {"github_issue": 355, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Обновлён до полного YAML-шаблона с подчёркнутыми социальными ветками."}, {"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Перенос концепта вертикального города в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

