-- Quest: canon-quest-ny-bridge-fortress
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-bridge-fortress', 'Нью-Йорк — Мост-крепость', 'Игроки укрепляют выбранный мост, собирают пошлины и сдерживают атаки конкурентов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-bridge-fortress", "title": "Нью-Йорк — Мост-крепость", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T14:55:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T14:55:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "gangs", "territory"], "topics": ["regional-content", "faction-warfare", "territorial-control"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "faction-service", "combat-service", "security-service", "social-service"], "related_documents": [{"id": "canon-quest-ny-brooklyn-bridge-battle", "relation": "foreshadows"}, {"id": "github-issue-355", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/355", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2040-2060/quest-023-bridge-fortress.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание мостовых банд хранилось в Markdown и не увязывалось с системами территориального контроля.", "goal": "Перенести мостовую осаду в YAML, описав доходы, штурмы и фракционные ставки.", "essence": "Игроки укрепляют выбранный мост, собирают пошлины и сдерживают атаки конкурентов.", "key_points": ["Расписаны оборонительные этапы, экономика пошлин и расписание рейдов оппонентов.", "Выстроены связи с системой пассивного дохода и PvP-контрольными зонами.", "Прописаны последствия победы/поражения для городской логистики и сюжетных веток."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: faction, сложность: hard, формат: группа 5-10 игроков, длительность: 2-4 часа.\\nНаграды: 3,000 XP, пассивный доход 5,000 едди/день, уникальные титулы и доступ к вооружению банд.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Заключить контракт с бандой Brooklyn Bridge и оценить текущие укрепления.\\n2. Развернуть турели, баррикады и сенсоры на въездах мостов, распределить смены охраны.\\n3. Организовать сбор пошлины с караванов и отслеживать нелегальные переправы.\\n4. Отбить координированную атаку конкурентов (PvPvE), используя ловушки и вызов подкреплений.\\n5. Закрепить власть, настроить логистические бонусы и открыть новые линии миссий.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Пять основных мостов Нью-Йорка превращены в крепости с блокпостами и рынками.\\nГотические башни Brooklyn Bridge служат штабами, а грузовые дроны обеспечивают снабжение.\\nТорговые караваны вынуждены платить \\"бриджерам\\" или переходить к подземным маршрутам.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Задействованы системы территорий, пассивных доходов и PvP-рейдов — мост становится сезонным объектом.\\nПобеда открывает доступ к контенту \\"Bridge Wars\\" и усиливает влияние фракции на логистику региона.\\nПровал переносит контроль соперникам, повышая цены на торговлю и блокируя квестовые маршруты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "New York Bridge Infrastructure", "link": "https://www.nyc.gov"}, {"title": "Urban Stronghold Design", "link": "https://www.popularmechanics.com"}], "decisions": []}, "implementation": {"github_issue": 355, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Расширены экономические и PvP-связи, документ приведён к новому шаблону."}, {"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Создан YAML-квест о мостовой крепости."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

