-- Quest: canon-quest-ny-jazz-club-underground
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-jazz-club-underground', 'Нью-Йорк — Подпольный джаз-клуб', 'Игрок обнаруживает закрытый джаз-клуб эпохи 2040-х, выступает на сцене и завоёвывает доверие музыкального подполья.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-jazz-club-underground", "title": "Нью-Йорк — Подпольный джаз-клуб", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:18:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:18:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "jazz", "nightlife"], "topics": ["culture", "social-encounters", "music"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "reputation-service", "culture-service", "music-system", "wellbeing-service"], "related_documents": [{"id": "github-issue-356", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/356", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2040-2060/quest-026-jazz-club-underground.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание подпольного джаз-клуба находилось в Markdown и не учитывало системные эффекты культуры и репутации.", "goal": "Структурировать квест о тайном джазовом вечере в нью-йоркском подвале, связав его с социальными механиками.", "essence": "Игрок обнаруживает закрытый джаз-клуб эпохи 2040-х, выступает на сцене и завоёвывает доверие музыкального подполья.", "key_points": ["Этапы поиска клуба, общения с владельцем и выступления перед избранной публикой.", "Добавлены системные крючки для mood- и reputation-system, влияющие на музыку и связи.", "Награды дают доступ к культурной сети и уникальным музыкальным бонусам."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: medium. Формат: solo. Длительность: 2–3 часа.\\nНаграды: 2 000 XP, 5 000 едди, +10 репутации у музыкального подполья, эксклюзивные контакты.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить наводку на секретный клуб и найти скрытый вход в подвал Гарлема.\\n2. Познакомиться с владельцем, доказать музыкальный вкус и заплатить вступительный взнос.\\n3. Пройти импровизированное прослушивание и выступить на сцене с джаз-бэндом.\\n4. Общаться с посетителями, обмениваться контактами и узнавать о подпольных мероприятиях.\\n5. Закрепить успех, записав живой сет и получить приглашение в элитные джазовые вечера.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "social-system управляет общением с музыкантами и гостями, reputation-system повышает статус в культурных кругах.\\nmood-system создаёт атмосферный эффект джаза, влияя на эмоции и бонусы к навыкам.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны кирпичные подземные комнаты, ароматы виски, тёплые светильники и постеры эпохи Harlem Renaissance.\\nУказаны живые импровизации, поэтические слэмы и скрытые входы через задний переулок.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успех даёт 2 000 XP, 5 000 едди и доступ к культурной сети джаз-клубов.\\nПровал лишает приглашений и снижает репутацию среди артистов подполья.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Harlem Jazz Clubs History", "link": "https://www.smithsonianmag.com"}, {"title": "Modern NYC Underground Venues", "link": "https://www.timeout.com/newyork"}], "decisions": []}, "implementation": {"github_issue": 356, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в шаблон 1.1.0, добавлены культурные и репутационные механики."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание подпольного джаз-клуба."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

