-- Quest: canon-quest-ny-hotdog-vendor
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-hotdog-vendor', 'Нью-Йорк — Продавец хот-догов', 'Игрок арендует тележку, работает на Таймс-сквер, продаёт настоящие хот-доги и соревнуется за прибыль.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-hotdog-vendor", "title": "Нью-Йорк — Продавец хот-догов", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:19:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:19:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "street_food", "slice_of_life"], "topics": ["economy", "everyday-life", "mini-games"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "wellbeing-service"], "related_documents": [{"id": "github-issue-357", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/357", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2040-2060/quest-027-hotdog-vendor.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о дне продавца хот-догов был в Markdown и не описывал экономические мини-игры и социальные эффекты.", "goal": "Структурировать работу уличного вендора, показать конкуренцию и атмосферу нью-йоркских тележек.", "essence": "Игрок арендует тележку, работает на Таймс-сквер, продаёт настоящие хот-доги и соревнуется за прибыль.", "key_points": ["Этапы аренды тележки, поиска места и продажи через мини-игру.", "Добавлены крючки для economy- и social-system, отражающие прибыль и отношения с клиентами.", "Награды дают опыт, заработок и титул «Честный труд»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 30–60 минут.\\nНаграды: 500 XP, 500–2 000 едди прибыли, ачивка «Честный труд», модификаторы репутации с уличными торговцами.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Арендовать тележку и загрузить ингредиенты: сосиски, sauerkraut, onions.\\n2. Найти выгодную точку (Times Square или Центральный парк) и получить разрешение.\\n3. Обслуживать клиентов через мини-игру, поддерживая качество и скорость.\\n4. Конкурировать с другими вендорами, участвовать в ценовой войне или договариваться о зонах.\\n5. Подвести итоги смены, подсчитать прибыль и решить, расширять ли бизнес.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "economy-system рассчитывает себестоимость и прибыль, social-system отражает отношения с клиентами и конкурентами.\\nbuff-system выдаёт временный бафф «Street Hustle», повышающий выносливость и удачу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны красно-жёлтые зонтики, запахи sauerkraut, «dirty water dogs» и постоянный поток туристов.\\nПодчёркнут нью-йоркский hustle, крики «Two dogs, please!» и конкуренция уличных торговцев.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успех приносит до 2 000 едди и ачивку, открывает скидки на ингредиенты и контакты других вендоров.\\nПровал или плохое обслуживание снижает репутацию и может привести к штрафам городских инспекторов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "NYC Street Vendor Guide", "link": "https://www1.nyc.gov"}, {"title": "History of Hot Dog Carts", "link": "https://www.nypl.org"}], "decisions": []}, "implementation": {"github_issue": 357, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в шаблон 1.1.0, добавлены экономические и социальные механики."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание работы продавца хот-догов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

