-- Quest: canon-quest-ny-yankees-stadium
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-yankees-stadium', 'Нью-Йорк — Стадион Янки', 'Игрок покупает билет, погружается в атмосферу стадиона Янки и разделяет фанатскую культуру во время ключевых моментов матча.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-yankees-stadium", "title": "Нью-Йорк — Стадион Янки", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T16:51:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T16:51:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "yankees", "baseball"], "topics": ["sports", "lifestyle", "tourism"], "related_systems": ["tourism-system", "mood-system", "economy-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-012-yankees-stadium.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание визита на Yankee Stadium находилось в Markdown и не фиксировало туристические награды и системные эффекты.", "goal": "Перевести опыт посещения бейсбольной игры в YAML, связав его с системами настроения, туризма и экономики.", "essence": "Игрок покупает билет, погружается в атмосферу стадиона Янки и разделяет фанатскую культуру во время ключевых моментов матча.", "key_points": ["Детализированы этапы подготовки, посещения и празднования победы команды.", "Добавлены системные крючки для mood-, tourism- и economy-system.", "Награды дают культурный бафф и продвигают спортивную ветку Нью-Йорка."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 600 XP, культурный бафф +5, титул «True Yankee Fan», расходы 100 едди.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Приобрести билет на игру и прибыть на стадион вместе с потоком фанатов.\\n2. Посетить сувенирные лавки, Monument Park и занять место на трибуне.\\n3. Совершить покупку классического hot dog и напитков, активировать модификатор настроения.\\n4. Наблюдать ключевые иннинги, участвовать в песне «Take Me Out to the Ball Game» во время seventh-inning stretch.\\n5. Праздновать победу Yankees или обсудить поражение с соседями по трибуне, влияя на репутацию среди фанатов.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "tourism-system начисляет очки посещения достопримечательностей, economy-system учитывает расходы на билет и еду.\\nmood-system повышает ностальгию и атмосферу, влияя на дальнейшие туристические квесты города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Подчёркнуты pinstripes формы, chants фанатов, Bleacher Creatures и традиция Monument Park.\\nОписаны визуальные и аудиальные маркеры стадиона: табло, сигнальные колокола и волна на трибунах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Посещение даёт 600 XP, культурный бафф и титул, а также открывает скидки у уличных продавцов в Бронксе.\\nПропуск ключевых моментов снижает эффект баффа и роняет доверие среди фанатских фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Yankee Stadium Visitor Guide", "link": "https://www.mlb.com/yankees/ballpark"}, {"title": "History of the New York Yankees", "link": "https://www.baseballhall.org"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Структурировано посещение Yankee Stadium и добавлены системные эффекты."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание визита на стадион."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

