INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-atlanta-southern-music-digital-evolution', 'Atlanta 2020-2029 — «Эволюция южной музыки в цифровую эру»', 'Atlanta как столица хип-хопа эволюционирует в эпоху, когда музыка становится прямым расширением нервной системы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-atlanta-southern-music-digital-evolution", "title": "Atlanta 2020-2029 — «Эволюция южной музыки в цифровую эру»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:55:30+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["atlanta", "music", "southern", "digital", "hip-hop"], "topics": ["music", "culture", "technology"], "related_systems": ["narrative-service", "music-system", "social-system"], "related_documents": [{"id": "canon-region-america-atlanta-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/atlanta/2020-2029/quest-015-southern-music-digital-evolution.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Музыкальная сцена Atlanta экспериментирует с нейро-музыкой, позволяющей слушателям чувствовать ритм через импланты.", "goal": "Игрок помогает артистам найти баланс между традиционным хип-хопом и инновационными технологиями.", "essence": "Atlanta как столица хип-хопа эволюционирует в эпоху, когда музыка становится прямым расширением нервной системы.", "key_points": ["Нейро-треки синхронизируются с мозговыми волнами слушателей.", "Конфликт между \\"чистым искусством\\" и коммерческими технологиями.", "Финальный фестиваль демонстрирует будущее музыки."]}, "content": {"sections": [{"id": "neuro-beats", "title": "Нейро-биты", "body": "Игрок посещает студию звукозаписи в Little Five Points. Продюсеры демонстрируют музыку, адаптирующуюся к эмоциям слушателя.\\n", "mechanics_links": [], "assets": []}, {"id": "artistic_integrity", "title": "Художественная целостность", "body": "Традиционные рэперы критикуют \\"техно-музыку\\" за потерю аутентичности. Игрок помогает найти компромисс.\\n", "mechanics_links": [], "assets": []}, {"id": "music_production", "title": "Музыкальное производство", "body": "Мини-игра: создание нейро-трека с элементами южного хип-хопа. Баланс между технологиями и человеческим талантом.\\n", "mechanics_links": [], "assets": []}, {"id": "southern_music_festival", "title": "Южный музыкальный фестиваль", "body": "Финальный фестиваль с выступлениями традиционных и нейро-артистов. Определение будущего музыкальной индустрии.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1000 XP.\\n- Издержки: −40 кредов.\\n- Бафф «Музыкальный нейрон +25% эффективности в творческих и развлекательных задачах на 15 часов».\\n- Ачивка «Цифровой диджей».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Neuro-Music", "definition": "Музыка, адаптирующаяся к нейронным паттернам слушателя через импланты для создания персонализированного опыта."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140875691, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Эволюция южной музыки в цифровую эру» для Atlanta 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, d