-- Quest: canon-quest-ny-american-dream
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-american-dream', 'Нью-Йорк — Американская мечта', 'Игрок начинает без средств, строит карьеру, открывает бизнес и решает, чем пожертвовать ради мечты.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-american-dream", "title": "Нью-Йорк — Американская мечта", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:22:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:22:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "american_dream", "morality"], "topics": ["economy", "progression", "philosophy"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "reputation-service", "social-service", "progression-service", "world-simulation"], "related_documents": [{"id": "github-issue-357", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/357", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2040-2060/quest-030-american-dream.md", "visibility": "internal", "audience": ["narrative", "systems", "content"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Философский квест о «американской мечте» был в Markdown и не описывал экономические ветки и моральные последствия.", "goal": "Структурировать путь от нуля до успеха в посткризисном Нью-Йорке, подчеркнув моральные выборы.", "essence": "Игрок начинает без средств, строит карьеру, открывает бизнес и решает, чем пожертвовать ради мечты.", "key_points": ["Этапы поиска работы, накопления капитала и открытия бизнеса.", "Добавлены системные крючки для economy- и reputation-system, отражающие разные пути.", "Исходы показывают цену успеха, честности или отказа от мечты."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main. Сложность: medium. Формат: solo. Длительность: 4+ часов.\\nНаграды: до 5 000 XP, 0–100 000 едди (по исходу), изменение репутации и мировоззрения.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Начать без средств, найти временную работу в Нью-Йорке (вендор, курьер, наёмник).\\n2. Накопить капитал, изучить рынок и решить, во что инвестировать.\\n3. Открыть бизнес или социальный проект, управлять сотрудниками и финансами.\\n4. Столкнуться с дилеммами: эксплуатация, честность или отказ от мечты.\\n5. Выбрать финал и закрепить последствия, влияющие на город и личную историю.\\n", "mechanics_links": [], "assets": []}, {"id": "decision_points", "title": "Ключевые решения", "body": "- Успех любой ценой: богатство, но потеря совести и ухудшение репутации у гражданских.\\n- Честный путь: умеренный доход, уважение людей и ограниченные ресурсы.\\n- Отказ: признать мечту мёртвой, сохранить независимость и открыть новые сюжеты сопротивления.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "economy-system моделирует капитал, налоги и инвестиции, reputation-system отслеживает отношение фракций.\\nnarrative-outcome-system фиксирует философские последствия и влияют на будущие сюжетные линии.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны затопленные районы Манхэттена, наследие Ellis Island и контраст между роскошью и трущобами.\\nПодчёркнуты лозунги «If I can make it here…» и цинизм «Dream is dead».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Выбор определяет 0–100 000 едди, мировоззренческую запись и отношение фракций.\\nУспех открывает элитные контракты, честность — поддержку общин, отказ — линии сопротивления.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "American Dream Cultural Analysis", "link": "https://www.brookings.edu"}, {"title": "Post-Crisis NYC Economic Reports", "link": "https://www1.nyc.gov"}], "decisions": []}, "implementation": {"github_issue": 357, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в шаблон 1.1.0, добавлены экономические ветки и моральные исходы."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание квеста «Американская мечта»."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

