-- Quest: canon-quest-ny-bagel-quest
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-bagel-quest', 'Нью-Йорк — Квест бейгла', 'Игрок исследует нью-йоркские пекарни, дегустирует разные бейглы и запускает вечный спор о лучшем рецепте.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-bagel-quest", "title": "Нью-Йорк — Квест бейгла", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T16:54:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T16:54:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "bagel", "cuisine"], "topics": ["gastronomy", "lifestyle", "community"], "related_systems": ["food-system", "mood-system", "tourism-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-015-bagel-quest.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут поиска лучшего бейгла был в Markdown и не подсвечивал кулинарные системы и репутационные эффекты.", "goal": "Перенести квест в YAML с детализацией дегустаций, спорящих NPC и баффов пищи.", "essence": "Игрок исследует нью-йоркские пекарни, дегустирует разные бейглы и запускает вечный спор о лучшем рецепте.", "key_points": ["Описаны этапы опроса жителей, дегустаций и выбора фаворита.", "Добавлены системные крючки для food-, mood- и tourism-system.", "Награды дают гастрономический бафф и продвигают кулинарную ветку города."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 30–60 минут.\\nНаграды: 400 XP, бафф «Нью-йоркский бейгл» (+15% HP на 6 часов), титул «Бейгл-эксперт», расходы 20 едди.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Опросить жителей разных районов и собрать список легендарных пекарен.\\n2. Посетить три пекарни, наблюдать процесс кипячения и выпечки, фиксируя вкусовые заметки.\\n3. Провести дегустацию вариантов (lox, schmear, poppy seed, sesame) и подсчитать очки вкуса.\\n4. Объявить победителя, презентовать результаты публике и высказать мнение в прямом эфире местного радио.\\n5. Подготовиться к волне споров, управляя репутацией среди кулинарных фракций и владельцев пекарен.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "food-system рассчитывает баффы и насыщение, mood-system повышает удовлетворённость и снижает стресс.\\ntourism-system добавляет очки гастрономическим маршрутам Нью-Йорка и открывает новые дегустационные квесты.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны еврейские пекарни, процесс кипячения и выпечки, сленг покупателей «schmear», «lox» и привычка спорить «fuhgeddaboutit».\\nВключены атмосфера утренних очередей, запах свежего теста и шум метро поблизости.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает бафф +15% HP на 6 часов, 400 XP и скидки в пекарнях-победителях.\\nВыбор фаворита может испортить отношения с проигравшими заведениями, открывая ветку кулинарных соперничеств.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "History of the New York Bagel", "link": "https://www.seriouseats.com"}, {"title": "Classic Bagel Shops Map", "link": "https://www.timeout.com/newyork"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлены системные крючки, гастрономические баффы и репутационные последствия."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание поиска лучшего бейгла."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

