INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-madison-square-garden', 'Нью-Йорк — Мэдисон-Сквер-Гарден', 'Игрок регистрируется на турнир, выступает перед 20 000 зрителей и либо берёт чемпионский титул, либо получает опыт.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-madison-square-garden", "title": "Нью-Йорк — Мэдисон-Сквер-Гарден", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:06:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:06:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "arena", "tournament"], "topics": ["regional-content", "competitive-events", "sports-culture"], "related_systems": ["tournament-system", "spectator-system", "rewards-system"], "related_documents": [{"id": "canon-quest-ny-red-sky-storm", "relation": "contrasts"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2061-2077/quest-032-madison-square-garden.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание турнира на MSG было в Markdown и не отражало связки с системой турниров и престижом арены.", "goal": "Перенести событие в YAML, зафиксировать этапы соревнования, награды и влияние на зрительскую экономику.", "essence": "Игрок регистрируется на турнир, выступает перед 20 000 зрителей и либо берёт чемпионский титул, либо получает опыт.", "key_points": ["Расписаны регистрация, подготовка, полуфиналы и финал на культовой арене.", "Обозначены награды для победителя и утешительные бонусы за участие.", "Встроены связи с системой зрителей, спонсорами и медиасобытиями."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: event, сложность: hard, формат: solo, длительность: 2-4 часа.\\nНаграды: победа — 100,000 едди, титул «MSG Champion», легендарный сет \\"Garden Glory\\"; участие — 5,000 XP.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Регистрация на турнир (боевой модуль или киберспорт), получение слотов тренировок.\\n2. Тренировки и разбор соперников вместе с аналитиком арены.\\n3. Выступление на арене перед 20,000 зрителей, динамические комментарии и трансляции.\\n4. Полуфинал и финал, где игрок сталкивается с элитными соперниками.\\n5. Подведение итогов: коронация чемпиона или запись в список претендентов на реванш.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Madison Square Garden остаётся \\"Самой известной ареной мира\\": купольная конструкция, Jumbotron и легендарные скандирования.\\nKnicks и Rangers делят календарь с киберспортивными шоу, а концерты мировых звёзд следуют за турнирами.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест использует систему турниров, зрительские рейтинги и контракты спонсоров.\\nПобеда открывает рекламные сделки и эксклюзивные ивенты, поражение — ветку тренировок и реваншей.\\nСобытие влияет на городские медиа и экономику мерча.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Madison Square Garden History", "link": "https://www.msg.com"}, {"title": "Esports Arena Case Study", "link": "https://www.theverge.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлены этапы турнира, награды и системное влияние."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Первичный перенос в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

