-- Quest: canon-quest-ny-empire-state-climb
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-empire-state-climb', 'Нью-Йорк — Восхождение на Эмпайр-стейт', 'Игрок проникает в Empire State Building, поднимается на шпиль и устанавливает хакерский узел под носом корп-охраны.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-empire-state-climb", "title": "Нью-Йорк — Восхождение на Эмпайр-стейт", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:14:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:14:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "empire_state", "infiltration"], "topics": ["vertical-gameplay", "hacking", "iconics"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "stealth-service", "hacking-service", "infiltration-service", "exploration-service"], "related_documents": [{"id": "github-issue-353", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/353", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2020-2029/quest-009-empire-state-climb.md", "visibility": "internal", "audience": ["narrative", "systems", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий взлома Эмпайр-стейт был в Markdown и не раскрывал вертикальные механики и опасности.", "goal": "Структурировать восхождение на небоскрёб в YAML, соединяя стелс, хакерство и культовое место.", "essence": "Игрок проникает в Empire State Building, поднимается на шпиль и устанавливает хакерский узел под носом корп-охраны.", "key_points": ["Расписаны этапы проникновения, подъёма и установки узла.", "Добавлены системные крючки для traversal-, stealth- и hacking-system, влияющие на риск и награды.", "Награды включают доступ к городскому узлу и титул, усиливая хакерскую линию Нью-Йорка."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side. Сложность: hard. Формат: solo. Длительность: 2–4 часа.\\nНаграды: 3 000 XP, 15 000 едди, ачивка «Король Кинг-Конг», бесплатный доступ к хакерскому узлу.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Проникнуть в нижние уровни Empire State, минуя охрану и сканеры.\\n2. Выбрать маршрут подъёма: скоростной лифт или 1 576 ступеней служебной лестницы.\\n3. Обезвредить патрули на крыше, избегая камер и дронов.\\n4. Закрепиться на шпиле, установить хакерский узел и синхронизировать его с сетью.\\n5. Покинуть здание прыжком с парашютом или zip-line, скрыв следы вмешательства.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "traversal-system моделирует подъём и удержание на высоте, stealth-system отслеживает обнаружение охраной.\\nhacking-system отвечает за установку узла и выдаёт бонусы к городской инфраструктуре.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны Art Deco интерьеры, смотровые площадки, ветер на высоте и подсветка, сменяющая цвета по праздникам.\\nУказаны отсылки к King Kong и панораме Манхэттена с крыши.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Награды дают 15 000 едди, 3 000 XP, ачивку и сетевой узел, обеспечивающий бесплатный интернет в регионе.\\nПровал приводит к аресту, штрафам и блокировке доступа к верхним уровням здания.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Empire State Building Facts", "link": "https://www.esbnyc.com"}, {"title": "High-Rise Security Guidelines", "link": "https://www.osha.gov"}], "decisions": []}, "implementation": {"github_issue": 353, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в шаблон 1.1.0, добавлены вертикальные механики и награды за установку узла."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание восхождения на Эмпайр-стейт."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

