-- Quest: canon-quest-ny-cheesecake-factory
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-cheesecake-factory', 'Нью-Йорк — Фабрика чизкейков', 'Игрок разыскивает легендарную фабрику чизкейков в Бруклине, узнаёт секрет рецепта и пробует десерт, повышающий настроение.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-cheesecake-factory", "title": "Нью-Йорк — Фабрика чизкейков", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:02:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:02:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "cheesecake", "cuisine"], "topics": ["gastronomy", "tradition", "family"], "related_systems": ["food-system", "mood-system", "economy-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-020-cheesecake-factory.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История о последней фабрике нью-йоркских чизкейков была в Markdown и не описывала кулинарные баффы и связи с общиной.", "goal": "Перенести квест в YAML, выделив гастрономические награды, семейные традиции и влияние на food-system.", "essence": "Игрок разыскивает легендарную фабрику чизкейков в Бруклине, узнаёт секрет рецепта и пробует десерт, повышающий настроение.", "key_points": ["Расписаны этапы поиска, знакомства с владельцами и дегустации.", "Добавлены системные крючки для food- и mood-system, влияющие на здоровье и эмоции.", "Награды дают длительный бафф и открывают кулинарные ветки города."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: medium. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 800 XP, бафф «Классический чизкейк» (+20% HP на 12 часов, счастье +50%), титул «Чизкейк-гурман», расходы 30 едди.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Собрать слухи о последней фабрике настоящих чизкейков и добраться до её скрытого входа в Бруклине.\\n2. Познакомиться с семейными владельцами, услышать историю бизнеса и посмотреть процесс выпечки.\\n3. Изучить ингредиенты (cream cheese, sour cream, graham cracker crust) и наблюдать за секретным этапом.\\n4. Попробовать свежевыпеченный чизкейк, активировать гастрономический бафф и записать отзыв.\\n5. Решить, раскрывать ли рецепт миру или сохранить традицию семьи, влияя на отношения с кулинарными фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "food-system рассчитывает бафф здоровья и насыщения, economy-system учитывает расходы и возможные инвестиции в фабрику.\\nmood-system повышает счастье и открывает кулинарные ивенты в Бруклине.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны плотная текстура чизкейка, охлаждённые витрины, еврейские семейные традиции и исторические фото Junior’s.\\nВключены звуки метро, ароматы свежего десерта и очереди поклонников сладостей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Квест даёт 800 XP, длительный бафф и доступ к эксклюзивным десертам; возможен партнёрский контракт с владельцами.\\nРазглашение секрета без согласия семьи ухудшает репутацию и закрывает бонусные рецепты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "History of New York Cheesecake", "link": "https://www.juniorscheesecake.com"}, {"title": "Classic Cheesecake Techniques", "link": "https://www.seriouseats.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлены системные крючки, гастрономические баффы и последствия раскрытия рецепта."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание квеста фабрики чизкейков."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

