-- Quest: canon-quest-ny-hip-hop-battle
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-hip-hop-battle', 'Нью-Йорк — Хип-хоп батл', 'Игрок вступает в импровизационный рэп-батл в South Bronx, пишет текст на лету и завоёвывает толпу, рискуя репутацией MC.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-hip-hop-battle", "title": "Нью-Йорк — Хип-хоп батл", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T16:50:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T16:50:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "bronx", "hip_hop"], "topics": ["urban-culture", "performance", "music"], "related_systems": ["music-minigame-system", "reputation-system", "economy-system"], "related_documents": [{"id": "canon-quest-ny-harlem-renaissance", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-011-hip-hop-battle.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Оригинальное описание хип-хоп батла существовало в Markdown и не отражало системные крючки и последствия.", "goal": "Структурировать событие Бронкса в YAML, подчеркнув механику рэп-батла, экономику взносов и влияние на репутацию.", "essence": "Игрок вступает в импровизационный рэп-батл в South Bronx, пишет текст на лету и завоёвывает толпу, рискуя репутацией MC.", "key_points": ["Расписаны этапы регистрации, подготовки текста и выступления перед аудиторией.", "Указаны системные связи с music-minigame-system и репутационными кривыми культурных фракций.", "Победа даёт титул «MC» и открывает городскую хип-хоп линию Нью-Йорка."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: event. Сложность: medium. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 10 000 едди, титул «MC», +10 репутации хип-хоп сцены, 1 000 XP за участие при поражении.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть на площадку в South Bronx, оплатить взнос 500 едди и пройти регистрацию.\\n2. Сыграть мини-игру по написанию текста под заданный бит, подбирая рифмы и панчи.\\n3. Выйти на сцену, провести раунд обменов диссами и реагировать на импровизации оппонента.\\n4. Поддерживать контакт с толпой, ловить beatbox и DJ-сэмплы, усиливать эффект crowd-control.\\n5. Дождаться голосования аудитории, получить титул или разобрать ошибки с наставником.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "music-minigame-system генерирует ритм и проверяет качество импровизации, economy-system списывает взнос и выдаёт приз.\\nreputation-system отслеживает реакцию локальных фракций и открывает новые выступления в Гарлеме и Бруклине.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны граффити на стенах, beatbox-эмси, b-boys и характерные выкрики «Yo, yo!» в толпе.\\nУчитывается культурный контекст Бронкса как родины хип-хопа и отсылка к легендарным батлам 1970-х.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Победа приносит 10 000 едди, титул «MC» и рост репутации, поражение оставляет 1 000 XP и доступ к тренировкам.\\nВыбор агрессивного стиля может ухудшить отношения с культурными лидерами, но повысить уважение у андеграундных фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "History of Bronx Rap Battles", "link": "https://www.npr.org"}, {"title": "Hip-Hop Culture Timeline", "link": "https://www.history.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Переведено в YAML-структуру, добавлены системные крючки и детализированы награды."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание рэп-батла в Бронксе."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

