-- Quest: canon-quest-ny-rockefeller-center
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-rockefeller-center', 'Нью-Йорк — Рождество в Rockefeller Center', 'Игрок проходит путь от подготовки к Рождеству до участия в церемонии зажжения ёлки и вечернего приема.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-rockefeller-center", "title": "Нью-Йорк — Рождество в Rockefeller Center", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:35:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:35:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "christmas", "tourism"], "topics": ["lifestyle", "seasonal_events", "urban_landmarks"], "related_systems": ["event-system", "mood-system", "reputation-system"], "related_documents": [{"id": "canon-quest-ny-times-square-chase", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2061-2077/quest-037-rockefeller-center.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о Рокфеллер-Центре существовал в Markdown и не раскрывал системные потоки рождественского события.", "goal": "Описать праздничный опыт катка и ёлки, чтобы интегрировать его в сезонные события и репутационные системы.", "essence": "Игрок проходит путь от подготовки к Рождеству до участия в церемонии зажжения ёлки и вечернего приема.", "key_points": ["Добавлены этапы подготовки, участия в церемонии и финального ужина в Rainbow Room.", "Системные крючки связывают праздничные баффы mood-system и репутацию культурных фракций.", "Награды теперь описывают XP, валюту и социальные бонусы."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo (кооператив необязателен). Длительность: 1–2 часа.\\nНаграды: 800 XP, -50 едди (затраты на каток), праздничный бафф настроения +50% на 24 часа, уникальная ачивка «Рождество в Нью-Йорке».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить приглашение на церемонию зажжения главной ёлки Rockefeller Center.\\n2. Подготовить экипировку для катка и прибыть к площадке к началу шоу.\\n3. Принять участие в церемонии зажжения, взаимодействуя с толпой и СМИ.\\n4. Выйти на каток, выполнить трюк перед золотой статуей Прометея и сделать обязательное фото.\\n5. Подняться в Rainbow Room, завершить вечер ужином и контактами с культурными фракциями города.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "event-system планирует появление квеста только в декабре, mood-system выдаёт праздничные эмоции.\\nreputation-system усиливает влияние на культурные и туристические фракции при успешном завершении этапов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Описаны гигантская ёлка, толпы туристов, рождественская музыка и традиции Rockefeller Plaza.\\nОтсылки к «Home Alone 2», золотой статуе Прометея и катку создают новогоднюю атмосферу Midtown Manhattan.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успех приносит праздничные баффы, раскрывает дополнительные зимние активности и укрепляет связи с рекламными агентствами.\\nПропуск церемонии или нарушение правил катка снижает репутацию и блокирует доступ к премиальному ужину.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Rockefeller Center Christmas Tree Lighting", "link": "https://www.rockefellercenter.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Обновлён до шаблона 1.1.0, прописаны системные крючки и праздничные награды."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Исходное Markdown-описание Рокфеллер-Центра."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

