-- Quest: canon-quest-ny-sopranos-reference
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-sopranos-reference', 'Нью-Йорк — Итальяно-американский заказ', 'Игрок выполняет контракт для итало-американской семьи, балансируя между лояльностью, моралью и выгодой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-sopranos-reference", "title": "Нью-Йорк — Итальяно-американский заказ", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:47:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:47:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "mafia", "pop_culture"], "topics": ["organized_crime", "cultural_references", "loyalty_tests"], "related_systems": ["faction-system", "narrative-choice-system", "reputation-system"], "related_documents": [{"id": "canon-quest-ny-mafia-family", "relation": "continues"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2061-2077/quest-038-sopranos-reference.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о мафиозной семье был в Markdown и не раскрывал решения и последствия для фракций.", "goal": "Формализовать мафиозный заказ с культурными отсылками к Sopranos, связав его с системами фракций и выбора.", "essence": "Игрок выполняет контракт для итало-американской семьи, балансируя между лояльностью, моралью и выгодой.", "key_points": ["Этапы встречи, выполнения заказа и финального банкетного ужина с семьёй.", "Системные крючки отображают последствия для faction-system и репутации мафии.", "Решения дают альтернативные награды и определяют долгосрочные отношения."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side. Сложность: medium. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 2 000 XP, 15 000 едди, +3 репутации у мафиозной фракции, доступ к подпольным торговцам.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Встретиться с донной фигурой в итальянском ресторане Little Italy и получить задание.\\n2. Проанализировать цель: запугивание, устранение или сбор долгов (варианты дают разные требования).\\n3. Выполнить заказ, избегая камер и полиции Нью-Джерси.\\n4. Вернуться в ресторан, предъявить доказательства и обсудить последствия.\\n5. Посетить банкет у семьи, закрепить отношения или нарушить традицию.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "faction-system отслеживает доверие мафии и ответные меры полиции.\\nnarrative-choice-system фиксирует моральные решения и открывает ветки сотрудничества или предательства.\\nreputation-system влияет на доступ к будущим криминальным сюжетам.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Культурные детали", "body": "Включены отсылки к сериалу «The Sopranos», фразам «Gabagool!» и «Fuhgeddaboudit!», красному соусу и канноли.\\nСцены распределены между ресторанами Little Italy, складом в Нью-Джерси и клубом с сигарами.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Положительное выполнение заказа усиливает доступ к мафиозной сети, открывает новый контракт и повышает доход.\\nПровал или двойная игра запускают охоту мафии, блокируют ресурсы и создают отрицательные новости в медиа.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "HBO The Sopranos", "link": "https://www.hbo.com/the-sopranos"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в шаблон 1.1.0, добавлены системные последствия и структура выбора."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-первичник с мафиозными отсылками."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

