-- Quest: quest-boston-2029-001-freedom-trail
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-boston-2029-001-freedom-trail', 'Бостон — Freedom Trail', 'Игрок проходит 4-километровый маршрут через 16 ключевых точек Бостона и формирует отношение к истокам Соединённых Штатов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-boston-2029-001-freedom-trail", "title": "Бостон — Freedom Trail", "document_type": "canon", "category": "narrative", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-22T19:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["boston", "freedom-trail", "history"], "topics": ["timeline_author", "american-revolution"], "related_systems": ["narrative-service", "tourism-service", "education-service"], "related_documents": [{"id": "github-issue-300", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/300", "relation": "migrated_to", "migrated_at": "2025-11-22T19:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-001-freedom-trail.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исторический маршрут Freedom Trail не оформлен в базе знаний и не связан с экономикой туризма и образовательными механиками.", "goal": "Структурировать квест по прохождению тропы свободы, чтобы подключить его к линии Американской революции и городским событиям.", "essence": "Игрок проходит 4-километровый маршрут через 16 ключевых точек Бостона и формирует отношение к истокам Соединённых Штатов.", "key_points": ["16 исторических локаций, объединённых красной линией Freedom Trail.", "Акцент на ролях Boston Common, Faneuil Hall, Old North Church и USS Constitution.", "Обучающий фокус на событиях 1775–1783 годов и отцах-основателях.", "Возможность использовать квест как триггер для туристических или политических событий."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает игроку пройти весь маршрут Freedom Trail, познакомившись с ключевыми местами Американской\\nреволюции. Он сочетает туристический опыт, образовательные элементы и эмоциональное погружение в историю города.\\n", "mechanics_links": []}, {"id": "regional_landmarks", "title": "Региональные отсылки", "body": "- Красная линия на тротуаре ведёт по 16 историческим объектам.\\n- Boston Common, Massachusetts State House, Granary Burying Ground, Old South Meeting House и Old State House\\n  раскрывают политический контекст.\\n- Место Boston Massacre и дом Пола Ревира подчёркивают личные истории революции.\\n- Старейший действующий корабль USS Constitution завершает маршрут в гаванях.\\n", "mechanics_links": []}, {"id": "quest_flow", "title": "Этапы прохождения", "body": "1. Начать в парке Boston Common и ознакомиться с легендой о Freedom Trail.\\n2. Следовать по красной линии, посещая State House, Park Street Church и Granary Burying Ground.\\n3. Изучить Old South Meeting House, Old State House и место Boston Massacre.\\n4. Посетить Faneuil Hall, Paul Revere House и Old North Church, чтобы увидеть истоки лозунга «Колыбель свободы».\\n5. Завершить маршрут у USS Constitution и проанализировать влияние морской мощи на революцию.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Завершение маршрута приносит 5000 опыта, повышает показатель «История» на 30 и открывает ачивку «Патриот\\nреволюции». Экономическая награда отсутствует, но квест повышает привлекательность региона для туристических\\nсобытий.\\n", "mechanics_links": []}, {"id": "mechanics", "title": "Механики и темы", "body": "Квест использует историческую и образовательную составляющие: игрок фиксирует ключевые даты, узнаёт об отцах-\\nоснователях и вовлекается в локальную туристическую активность.\\n", "mechanics_links": []}, {"id": "connections", "title": "Связи и расширения", "body": "Freedom Trail поддерживает революционную линию Бостона и служит стартом для эвентов с участием NPC-гидов, туристических сервисов и фракций, защищающих историческое наследие.\\n", "mechanics_links": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 300, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конверсия квеста Freedom Trail в структурированный YAML-формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

