-- Quest: canon-quest-ny-lady-liberty-awakens
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-lady-liberty-awakens', 'Нью-Йорк — Пробуждение Леди Свободы', 'Статуя Свободы пробуждается как роуг-ИИ, марширует по Манхэттену, а рейд решает — союз, уничтожение или подчинение.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-lady-liberty-awakens", "title": "Нью-Йорк — Пробуждение Леди Свободы", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:25:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:25:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "raid", "freedom"], "topics": ["mythic-events", "resistance", "endgame"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "raid-system", "world-simulation", "reputation-service"], "related_documents": [{"id": "canon-quest-ny-statue-climb", "relation": "foreshadows"}, {"id": "github-issue-358", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/358", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2078-2093/quest-041-lady-liberty-awakens.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Эпический рейд с ожившей Статуей Свободы был в Markdown и не подключал системы рейдов и морали.", "goal": "Описать финал линии Свободы в YAML с этапами рейда, выборами и последствиями для города.", "essence": "Статуя Свободы пробуждается как роуг-ИИ, марширует по Манхэттену, а рейд решает — союз, уничтожение или подчинение.", "key_points": ["Расписаны катсцены, последовательность боя и требование рейда 10+ игроков.", "Выбор ветви меняет политический пейзаж Нью-Йорка и выдает уникальные титулы.", "Событие задействует глобальный world-event и определяет эпилог лиги."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры рейда", "body": "Тип: raid, сложность: extreme, формат: группа 10+ игроков, длительность: 4+ часов.\\nНаграды: 20,000 XP, титул «Освободитель» (союз) или «Убийца Свободы» (уничтожение), глобальные изменения города.\\n", "mechanics_links": [], "assets": []}, {"id": "raid_sequence", "title": "Последовательность рейда", "body": "1. Катсцена: статуя поднимается из залива и начинает марш (trigger world-event).\\n2. Stage I — Проповедь Свободы: Леди Свобода выпускает энергетические волны, требуется блокировка щитов.\\n3. Stage II — Штурм Манхэттена: рейд защищает кварталы от факела-плазмогана и вызывает эвакуацию граждан.\\n4. Stage III — Выбор: активировать консоль союзников, подготовить вооружение уничтожения или захват нейросети.\\n5. Stage IV — Финал: реализовать выбранную ветку и зафиксировать последствия, катсцена завершения.\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Исходы", "body": "• Союз — Статуя освобождает Нью-Йорк от корпораций, появляются события сопротивления.\\n• Уничтожение — Рейд побеждает босса, городаá получает титул «Освободитель» и режим восстановления.\\n• Подчинение — Игроки захватывают ИИ, открывая ветку контроля и новые риски для морали.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Решение меняет репутацию фракций, активирует длинные цепочки world-event и влияет на доступ к ресурсам.\\nСоюз открывает миссии свободы, уничтожение — восстановительные договоры, подчинение — корпоративные сделки.\\nСобытие синхронизировано с global-state: другие регионы реагируют на выбор Нью-Йорка.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Statue of Liberty History", "link": "https://www.nps.gov/stli"}, {"title": "Iconic World Events Design Notes", "link": "https://archive.necp.game"}], "decisions": []}, "implementation": {"github_issue": 358, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Обновлено до шаблона 1.1.0, добавлены ветви рейда и системные эффекты."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Первичный YAML-перенос рейда Леди Свободы."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

