INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-league-constitution', 'Нью-Йорк — Конституция Лиги', 'Игрок участвует в конвенции City Hall, продвигает правила, участвует в дебатах и подписывает новую конституцию.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-league-constitution", "title": "Нью-Йорк — Конституция Лиги", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:26:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:26:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "constitution", "governance"], "topics": ["league-governance", "democracy", "meta-rules"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "governance-system", "diplomacy-service", "reputation-service", "social-service"], "related_documents": [{"id": "canon-quest-ny-american-legacy", "relation": "complements"}, {"id": "github-issue-358", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/358", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2078-2093/quest-042-league-constitution-ny.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Создание Конституции Лиги описывалось в Markdown и не подключало системы голосования и правил сезона.", "goal": "Структурировать квест в YAML с этапами конвенции, голосования и влияния на следующую лигу.", "essence": "Игрок участвует в конвенции City Hall, продвигает правила, участвует в дебатах и подписывает новую конституцию.", "key_points": ["Этапы охватывают предложения, жаркие дебаты и финальное голосование.", "Награды дают влияние и возможность изменить глобальные правила.", "Квест синхронизирован с системой league-constitution."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main, сложность: medium, формат: solo, длительность: 2-4 часа.\\nНаграды: 3,000 XP, влияние на правила следующей лиги, доступ к титулу «Father of the Constitution».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Созвать конвенцию в City Hall и ознакомиться с заявками фракций.\\n2. Формировать свой пакет правил, заручившись поддержкой союзников.\\n3. Вести публичные дебаты в американском стиле: громко, страстно, с использованием риторики.\\n4. Провести демократическое голосование, подсчитать результаты и учесть фракционные бонусы.\\n5. Подписать новую конституцию лиги пером-символом и опубликовать документ в league-repository.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест взаимодействует с voting-system: игроки формируют правила, влияющие на следующий сезон.\\nРезультат меняет отношение фракций и открывает дополнительные ветки governance-контента.\\nПровал инициативы снижает доверие и активирует оппозиционные миссии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "US Constitution Inspiration", "link": "https://www.archives.gov"}, {"title": "Democratic Process in MMOs", "link": "https://gamedesign.stackexchange.com"}], "decisions": []}, "implementation": {"github_issue": 358, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Документ обновлён до шаблона 1.1.0, оформлены этапы и системные связи."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Исходный YAML-перенос конституции лиги."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

