-- Quest: canon-quest-ny-high-line-walk
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-high-line-walk', 'Нью-Йорк — Прогулка по Хай-Лайн', 'Игрок поднимается на надземный парк, наслаждается садами и видами Манхэттена, снижая стресс.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-high-line-walk", "title": "Нью-Йорк — Прогулка по Хай-Лайн", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:23:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:23:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "high_line", "tourism"], "topics": ["regional-content", "urban-park", "relaxation"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "wellbeing-service", "social-service"], "related_documents": [{"id": "canon-quest-ny-central-park", "relation": "complements"}, {"id": "github-issue-359", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/359", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2078-2093/quest-045-high-line-walk.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут по High Line находился в Markdown и не задействовал туристические и фото-системы.", "goal": "Структурировать прогулочный квест в YAML, зафиксировать достопримечательности и релакс-эффекты.", "essence": "Игрок поднимается на надземный парк, наслаждается садами и видами Манхэттена, снижая стресс.", "key_points": ["Описаны ключевые точки маршрута и арт-инсталляции.", "Награды дают эффект релакса и стиль.", "Квест связывает туристические и фото-ивенты города."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 30-60 минут.\\nНаграды: 600 XP, -50% стресса, +5 к атрибуту «Стиль», ачивка «Хай-Лайн Путешественник».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Подняться на High Line с одной из лестниц в районе Chelsea.\\n2. Пройти по маршруту 2,3 км, делать фотоснимки арт-объектов и посадок.\\n3. Остановиться в зоне кафе и понаблюдать за городом.\\n4. Посетить обзорную площадку, оценить вид на Hudson Yards.\\n5. Завершить прогулку у Гудзона, зафиксировать впечатления и активировать бонус релакса.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "High Line — бывшая товарная железная дорога, преобразованная в парк на высоте 9 метров: сады, арт-инсталляции и кафе.\\nМаршрут проходит через Chelsea и Hudson Yards, сочетая индустриальные декорации и современный ландшафтный дизайн.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест подключает туристическую экономику, фото-ивенты и систему настроения.\\nУспешное прохождение открывает тематические экскурсии и скидки у местных продавцов.\\nПровал (отказ от прогулки) не даёт бонус релакса и закрывает часть событий.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "The High Line Park History", "link": "https://www.thehighline.org"}, {"title": "Hudson Yards Redevelopment", "link": "https://www.nyc.gov"}], "decisions": []}, "implementation": {"github_issue": 359, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-ny-high-line-walk"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлены системные связи и эффекты релакса."}, {"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Прогулка по Хай-Лайн» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

