-- Quest: canon-quest-ny-red-sky-storm
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-red-sky-storm', 'Нью-Йорк — Красная буря', 'Игрок получает предупреждение, укрывается в небоскрёбе, помогает пострадавшим и участвует в зачистке последствий.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-red-sky-storm", "title": "Нью-Йорк — Красная буря", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T14:56:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T14:56:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "storm", "event"], "topics": ["regional-content", "live-events", "climate-impact"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "liveops-service", "social-service", "weather-system", "world-simulation"], "related_documents": [{"id": "canon-timeline-2020-2040-destruction-recovery", "relation": "references"}, {"id": "github-issue-356", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/356", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2040-2060/quest-024-red-sky-storm.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Событие красной бури описывалось в Markdown и не подключало глобальный ивентовый фреймворк.", "goal": "Описать в YAML переживание пылевой бури Time of the Red с акцентом на помощь гражданам и экономику восстановления.", "essence": "Игрок получает предупреждение, укрывается в небоскрёбе, помогает пострадавшим и участвует в зачистке последствий.", "key_points": ["Выделены стадии предупреждения, пережидания бури и послебуревой помощи.", "Добавлены награды-свидетельства и репутация городского резерва волонтёров.", "Встроены связи с серверным эвентом и экономикой строителей."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: event, сложность: medium, формат: solo, длительность: 1-2 часа.\\nНаграды: 1,500 XP, титул «Переживший Красный Нью-Йорк», купоны на восстановительные услуги.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить сигнал от городских служб и подготовить укрытие на верхних уровнях небоскрёба.\\n2. Наблюдать за наступлением красного тумана, поддерживать фильтры и энергосистемы здания.\\n3. Помочь раненым и установить аварийное освещение в коридорах.\\n4. После спада бури очистить смотровые площадки, восстановить коммуникации и оценить ущерб.\\n5. Принять участие в городском брифинге, определяющем дальнейшие контракты и эвенты.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Красная пыль накрывает Манхэттен, неоновые вывески пробиваются через туман, респираторы обязательны.\\nTimes Square переливается красным, а Empire State служит маяком для спасательных дронов.\\nГородские легенды называют Нью-Йорк \\"Red Apple\\" в честь нового цвета неба.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест активирует глобальный live-event, влияющий на цены ресурсов и доступность транспорта.\\nИтоги события открывают сезонные миссии по строительству куполов и контрактам со страховщиками.\\nИгроки получают доступ к особым косметическим наградам и каналам оповещений.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Time of the Red Atmospheric Events", "link": "https://redskyarchive.net"}, {"title": "Dust Storm Preparedness NYC", "link": "https://www1.nyc.gov"}], "decisions": []}, "implementation": {"github_issue": 356, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Сценарий красной бури приведён к новому шаблону и расширен глобальными связями."}, {"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Создан YAML для события красной бури."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

