INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-empire-state-of-mind', 'Нью-Йорк 2078-2093 — Empire State of Mind', 'Игрок поднимается на смотровую площадку Empire State Building, делает ключевой legacy-выбор, наблюдает катсцену под музыку
Jay-Z и Alicia Keys и завершает пятилетний цикл Нью-Йорка.
', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-empire-state-of-mind", "title": "Нью-Йорк 2078-2093 — Empire State of Mind", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["new_york", "finale", "legacy"], "topics": ["league-conclusion", "emotional-finale"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "legacy-system", "culture-service", "social-service", "wellbeing-service"], "related_documents": [{"id": "canon-quest-ny-goodbye", "relation": "follows"}, {"id": "github-issue-360", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/360", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/new-york/2078-2093/quest-050-empire-state-of-mind.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Главный финал Нью-Йорка и переход к новой лиге не был представлен в структуре знаний; отсутствовали описания legacy-выбора.", "goal": "Зафиксировать катсцену на Empire State Building, выбор наследия и связь с песней «Empire State of Mind».", "essence": "Игрок поднимается на смотровую площадку Empire State Building, делает ключевой legacy-выбор, наблюдает катсцену под музыку\\nJay-Z и Alicia Keys и завершает пятилетний цикл Нью-Йорка.\\n", "key_points": ["Legacy-выбор определяет, что герой заберёт в следующую лигу.", "Катсцена закрепляет дух Нью-Йорка и звучание «Empire State of Mind».", "Завершение всех 50 квестов фиксируется как достижение и открывает новую лигу."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Финальный квест Нью-Йорка завершает арку 2020-2093 годов, объединяя культурное наследие, музыку и эмоциональный итог\\nмегаполиса.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Принять решение о legacy-наследии: какие навыки, связи или ресурсы переходят в следующую лигу.\\n2. Подняться на Empire State Building и приготовиться к финальной сцене.\\n3. Открыть катсцену, где играет «Empire State of Mind» и город мерцает.\\n4. Записать финальную реплику и подготовиться к перезапуску.\\n", "mechanics_links": [], "assets": []}, {"id": "cutscene", "title": "Катсцена", "body": "Персонаж наблюдает за огнями Manhattan skyline, музыка «Empire State of Mind» создаёт эмоциональный фон. Голос за кадром:\\n«Лига завершена. Нью-Йорк никогда не спит. See you on the other side, choom.» Экран уходит в золотой цвет.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест не выдаёт XP, но активирует legacy-переход, присваивает титул «Ветеран Нью-Йорка 2020-2093» и открывает новую лигу.\\nДостижение фиксирует прохождение всех 50 квестов.\\n", "mechanics_links": [], "assets": []}, {"id": "postscript", "title": "Послесловие", "body": "Перечислены все временные арки Нью-Йорка (2020-2093) и подтверждается завершение каждого десятилетия. Цитируется строка\\n«If I can make it there, I''ll make it anywhere» как итоговый мотив.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 360, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-ny-empire-state-of-mind"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация финального квеста «Empire State of Mind» в YAML и документирование legacy-сцены."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

