INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-empire-state-of-mind', 'Нью-Йорк — Empire State of Mind', 'Игрок поднимается на смотровую площадку, делает ключевой legacy-выбор и наблюдает катсцену под «Empire State of Mind».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-empire-state-of-mind", "title": "Нью-Йорк — Empire State of Mind", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:27:30+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:27:30+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "finale", "legacy"], "topics": ["league-conclusion", "emotional-finale", "legacy-system"], "related_systems": ["legacy-system", "narrative-service", "cutscene-system"], "related_documents": [{"id": "canon-quest-ny-goodbye", "relation": "precedes"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-yорк/2078-2093/quest-050-empire-state-of-mind.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальный квест Нью-Йорка не фиксировал legacy-выбор и катсцену в структуре знаний.", "goal": "Сформировать YAML-описание кульминации на Empire State Building, связав её с системой наследия.", "essence": "Игрок поднимается на смотровую площадку, делает ключевой legacy-выбор и наблюдает катсцену под «Empire State of Mind».", "key_points": ["Legacy-выбор определяет, что игрок заберёт в следующую лигу.", "Катсцена закрепляет дух Нью-Йорка и завершает 50 квестов.", "Квест открывает переход к новой лиге и выдаёт титул «Ветеран Нью-Йорка 2020-2093»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main, сложность: easy, формат: solo, длительность: 30-60 минут.\\nНаграды: титул «Ветеран Нью-Йорка 2020-2093», запись legacy-наследия, вывод финальной катсцены.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Собрать legacy-артефакты и решить, что переносить в следующую лигу.\\n2. Подняться на Empire State Building, подготовить сцену.\\n3. Активировать музыкальную катсцену с песней Jay-Z и Alicia Keys.\\n4. Наблюдать золотой мерцающий skyline, фиксируя достижения.\\n", "mechanics_links": [], "assets": []}, {"id": "legacy_outcome", "title": "Наследие", "body": "Игрок выбирает одно из наследий: боевое, социальное или экономическое. Выбор определяет стартовые бонусы следующей лиги.\\nКатсцена фиксирует решение, обновляет league-record и добавляет игрока в Hall of Fame Нью-Йорка.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Empire State Building Observatory", "link": "https://www.esbnyc.com"}, {"title": "Empire State of Mind Song History", "link": "https://www.billboard.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Документ обновлён под шаблон 1.1.0, описаны legacy-решения и катсцена."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Первичный YAML-перенос финального квеста."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requireme