-- Quest: canon-quest-ny-fifth-avenue-luxury
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-fifth-avenue-luxury', 'Нью-Йорк — Роскошь Пятой Авеню', 'Игрок проникает на закрытое мероприятие, вступает в разговоры с элитой и решает — влиться, ограбить или разоблачить систему.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-fifth-avenue-luxury", "title": "Нью-Йорк — Роскошь Пятой Авеню", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:05:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:05:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "luxury", "social_drama"], "topics": ["regional-content", "elite-culture", "social-dilemmas"], "related_systems": ["social-system", "reputation-system", "heist-system"], "related_documents": [{"id": "canon-quest-ny-vertical-city", "relation": "parallels"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2061-2077/quest-031-fifth-avenue-luxury.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий Fifth Avenue существовал только в Markdown и не раскрывал выбор игрока между статусом, преступлением и разоблачением.", "goal": "Структурировать элитный квест в YAML, подчеркнув социальные механики, репутационные эффекты и альтернативные финалы.", "essence": "Игрок проникает на закрытое мероприятие, вступает в разговоры с элитой и решает — влиться, ограбить или разоблачить систему.", "key_points": ["Расписаны этапы доступа к элитным бутикам и проверки этикета.", {"Награды зависят от выбранной финальной ветки": "статус, финансовая выгода или репутация активиста."}, "Интеграция с системами репутации, хищения и журналистских расследований."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: medium, формат: solo, длительность: 2-4 часа.\\nНаграды: 2,000 XP, изменение репутации элиты +5 (влитие) или -10 (разоблачение), до +200,000 едди при ограблении.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить приглашение на закрытое событие и пройти фейс-контроль на входе в Tiffany''s.\\n2. Взаимодействовать с элитой, поддерживая small talk и выполняя проверки этикета.\\n3. Исследовать бутики Fifth Avenue, изучая их безопасность и скрытые проходы.\\n4. Сделать выбор: присоединиться к элите, организовать хищение или собрать компромат.\\n5. Скрыть следы и встретиться с контактами, чтобы зафиксировать последствия.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Fifth Avenue остаётся символом роскоши: лимузины, витрины с бриллиантами и небоскрёбы, сверкающие золотом.\\nDoormen и частная охрана контролируют поток гостей, а медиа охотятся за сенсациями.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Выбор игрока влияет на репутацию элиты и открывает связки с хейстами или разоблачениями.\\nУспешное ограбление запускает контракты криминальных фракций, разоблачение — миссии журналистов и активистов.\\nВливание в элиту даёт скидки и приглашения на новые мероприятия.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Fifth Avenue Luxury Retail Report", "link": "https://www.forbes.com"}, {"title": "High Society Etiquette Guide", "link": "https://www.vanityfair.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Расширены ветки выбора, описание сцен и системные связи."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Стартовая конверсия квеста в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

