INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-philadelphia-ben-franklin', 'Филадельфия 2020-2029 — «Бенджамин Франклин»', 'Игрок исследует дом, музей и могилу Франклина, собирает изобретения и завершает квест с бонусами к науке.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-philadelphia-ben-franklin", "title": "Филадельфия 2020-2029 — «Бенджамин Франклин»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["philadelphia", "history", "enlightenment"], "topics": ["historical-exploration", "educational-content"], "related_systems": ["narrative-service", "history-system", "achievement-system"], "related_documents": [{"id": "canon-region-america-philadelphia-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/philadelphia/2020-2029/quest-006-ben-franklin.md", "visibility": "internal", "audience": ["narrative", "quest-design", "localization"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Оригинальное описание квеста не раскрывал маршруты, объекты экспозиции и связь с линией науки Филадельфии.", "goal": "Передать игроку маршрут по ключевым точкам Франклина, подкрепить механики науки и исторической идентичности города.", "essence": "Игрок исследует дом, музей и могилу Франклина, собирает изобретения и завершает квест с бонусами к науке.", "key_points": ["Городская экспедиция по Franklin Court, музею и Christ Church Burial Ground.", {"Сбор артефактов об изобретениях": "громоотвод, бифокалы, печь."}, "Социальная награда «Ученик Франклина» укрепляет научную ветку."]}, "content": {"sections": [{"id": "route", "title": "Маршрут и локации", "body": "Franklin Court служит точкой входа: игрок сканирует реконструированный дом и изучает типографию.\\nДальше музей предлагает интерактивные стенды об опыте с воздушным змеем, бифокалах и печи Франклина.\\nФинал — посещение могилы на Christ Church Burial Ground и традиция бросить монеты на плиту.\\n", "mechanics_links": [], "assets": []}, {"id": "interactions", "title": "Игровые взаимодействия", "body": "- Сканеры AR раскрывают «электрический» эксперимент и добавляют модификатор к навыку «Наука».\\n- Мини-игра в типографии позволяет напечатать листовку с афоризмами «Poor Richard''s Almanack».\\n- На кладбище игрок выбирает: сохранить традицию и бросить монеты или оставить послание.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP, -10 кредов (пожертвования) и +20 к параметру «Наука».\\n- Ачивка «Ученик Франклина» открывает доступ к научной ветке Филадельфии.\\n- Легендарный сувенир «Громоотвод Франклина» усиливает электрические сценарии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Franklin Court", "definition": "Музейный комплекс на месте дома Бенджамина Франклина в историческом центре Филадельфии."}, {"term": "Christ Church Burial Ground", "definition": "Кладбище, где похоронены Франклин и другие отцы-основатели, традиция бросать монеты приносит удачу."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Бенджамин Франклин» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

