-- Quest: canon-quest-ny-statue-climb
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-statue-climb', 'Нью-Йорк — Восхождение на Статую', 'Игроки добираются до наполовину затопленной статуи, поднимаются по внутренним конструкциям и активируют узел свободы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-statue-climb", "title": "Нью-Йорк — Восхождение на Статую", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:09:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:09:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "liberty-statue", "hacking"], "topics": ["regional-content", "climate-impact", "insurgency"], "related_systems": ["traversal-system", "hacking-system", "broadcast-system"], "related_documents": [{"id": "canon-quest-ny-flooded-manhattan", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2061-2077/quest-035-statue-climb.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Символический хайст на Статую Свободы хранился в Markdown и не описывал соединение traversal и хакерских систем.", "goal": "Перенести восхождение в YAML, подчеркнув климатическую метафору и распространение свободного интернета.", "essence": "Игроки добираются до наполовину затопленной статуи, поднимаются по внутренним конструкциям и активируют узел свободы.", "key_points": [{"Показаны фазовые изменения среды": "вода, ржавые лестницы, риск обрушения."}, "Установка узла даёт социальный бонус и запускает события в сетях города.", "Прописаны связи с анархистскими фракциями и климатическим нарративом."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side, сложность: hard, формат: группа 2-4 игроков, длительность: 2-4 часа.\\nНаграды: 3,000 XP, глобальный бафф «Free Internet for All», репутация повстанцев +15.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Доплыть до Статуи Свободы на катере, минуя патрули и течения.\\n2. Пробраться внутрь через факел, используя крюки и страховку.\\n3. Спуститься по частично затопленной лестнице, собрать данные о состоянии конструкции.\\n4. Установить хакерский узел в короне, подключить его к городским сетям.\\n5. Активировать трансляцию «Free Internet for All» и эвакуироваться до прибытия сил безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Статуя Свободы стоит по пояс в воде, медь окислилась, но факел продолжает светить.\\nПанорама затопленного Манхэттена напоминает о климатических изменениях и борьбе за свободу.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест соединяет traversal, хакерство и вещание: узел меняет сетевую карту города.\\nСвобода интернета открывает новые ветки в социальном и квестовом контенте.\\nПровал активирует ответную операцию безопасности и временно блокирует порталы в Нижний Манхэттен.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Statue of Liberty Climate Impact", "link": "https://www.nps.gov/stli"}, {"title": "Post-Climate New York Scenarios", "link": "https://www.climaterealityproject.org"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлена интеграция traversal/хакерства и социальный эффект от запуска узла."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Первый YAML-перенос квеста."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

