INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-philadelphia-brotherly-love', 'Филадельфия 2020-2029 — «Город братской любви»', 'Игрок исследует Penn''s Landing и City Hall, встречает историков-квакеров и решает — поддержать идеал братской любви или разоблачить лицемерие.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-philadelphia-brotherly-love", "title": "Филадельфия 2020-2029 — «Город братской любви»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["philadelphia", "philosophy", "founding"], "topics": ["civic-identity", "ethical-choice"], "related_systems": ["narrative-service", "reputation-system", "dialogue-system"], "related_documents": [{"id": "canon-region-america-philadelphia-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/philadelphia/2020-2029/quest-010-brotherly-love.md", "visibility": "internal", "audience": ["narrative", "quest-design", "localization"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест не раскрывал философский контекст названия Филадельфии и выбор игрока между идеалом и реальностью.", "goal": "Сформировать гражданскую экскурсию с диалоговыми выборами и проработкой квакерских принципов.", "essence": "Игрок исследует Penn''s Landing и City Hall, встречает историков-квакеров и решает — поддержать идеал братской любви или разоблачить лицемерие.", "key_points": ["Этимология «φιλαδελφία» раскрывается через интерактивные панели.", "Квакеры и наследие Уильяма Пенна задают моральные дилеммы.", "Выбор влияет на репутацию во фракциях «Идеалисты» и «Реалисты»."]}, "content": {"sections": [{"id": "historical_context", "title": "Исторический контекст", "body": "Гид ведёт игрока по Penn''s Landing, рассказывая о прибытии Уильяма Пенна в 1682 году и квакерских принципах религиозной свободы.\\n", "mechanics_links": [], "assets": []}, {"id": "dialogue_choice", "title": "Диалоговые ветки", "body": "- Идеалисты отстаивают первоначальную философию братской любви и толерантности.\\n- Реалисты указывают на исторические противоречия и сегрегацию.\\n- Финальный выбор фиксирует сторону и меняет репутацию фракций.\\n", "mechanics_links": [], "assets": []}, {"id": "conclusion", "title": "Завершение и награды", "body": "- 1 500 XP и +20 к параметру «Философия».\\n- Ачивка «Брат Филадельфиец» открывает гражданские квесты.\\n- Итоговый манифест игрока отображается на городском портале.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "William Penn", "definition": "Основатель Филадельфии, квакер, который заложил принципы религиозной свободы и гражданского братства."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Город братской любви» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

