INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-atlanta-2020-2029-mlk-historic-site', 'Атланта 2020-2029 — Исторический район MLK', 'Игрок посещает дом рождения MLK, церковь Ebenezer и King Center, переживая эмоциональную драму движения.', 'main', 10, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_birth_home", "text": "Посетить дом рождения MLK на Auburn Avenue и изучить экспозицию 1929 года", "type": "interact", "target": "mlk_birth_home", "count": 1, "optional": false}, {"id": "visit_ebenezer_church", "text": "Отправиться в церковь Ebenezer Baptist Church, где лидер проповедовал и обучал", "type": "interact", "target": "ebenezer_baptist_church", "count": 1, "optional": false}, {"id": "explore_king_center", "text": "Исследовать King Center: мемориал, могилу MLK и Coretta Scott King, вечный огонь и музей движения", "type": "explore", "target": "king_center", "count": 4, "optional": false}, {"id": "listen_dream_speech", "text": "Прослушать запись речи «I Have a Dream», активировав эмоциональный триггер", "type": "interact", "target": "i_have_a_dream_speech", "count": 1, "optional": false}, {"id": "complete_reflection", "text": "Завершить квест, осознавая борьбу за гражданские права и текущие социальные вызовы", "type": "interact", "target": "civil_rights_reflection", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "money": 0, "items": [{"item_id": "achievement_dreamer", "quantity": 1}], "reputation": {}, "unlocks": ["civil_rights_quests", "social_justice_access"]}'::jsonb, '[{"branch_id": "embrace_dream", "description": "Принять идеалы мечты", "consequences": {"skills": {"social_justice": 35, "empathy": 25}, "reputation": {"Civil_Rights_Movement": 30}, "unlocks": ["advanced_social_justice_quests"]}}]'::jsonb, '{"metadata": {"id": "canon-quest-atlanta-2020-2029-mlk-historic-site", "title": "Атланта 2020-2029 — Исторический район MLK", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["america", "atlanta", "quest"], "topics": ["timeline-author", "civil-rights"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "canon-lore-america-atlanta-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/atlanta/2020-2029/quest-002-mlk-historic-site.md", "visibility": "internal", "audience": ["lore", "narrative", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест по историческому району Мартина Лютера Кинга был в Markdown и не раскрывал системным образом ключевые точки.", "goal": "Структурировать посещение Martin Luther King Jr. National Historical Park в YAML, акцентируя борьбу за гражданские права.", "essence": "Игрок посещает дом рождения MLK, церковь Ebenezer и King Center, переживая эмоциональную драму движения.", "key_points": ["Дом рождения, церковь и мемориал сопровождаются контентом о движении за гражданские права.", "Вечный огонь и музей усиливают эмоциональное влияние.", "Квест повышает социальную справедливость и поддерживает социальную линию Атланты."]}, "quest_definition": {"quest_type": "main", "level_min": 10, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_birth_home", "text": "Посетить дом рождения MLK на Auburn Avenue и изучить экспозицию 1929 года", "type": "interact", "target": "mlk_birth_home", "count": 1, "optional": false}, {"id": "visit_ebenezer_church", "text": "Отправиться в церковь Ebenezer Baptist Church, где лидер проповедовал и обучал", "type": "interact", "target": "ebenezer_baptist_church", "count": 1, "optional": false}, {"id": "explore_king_center", "text": "Исследовать King Center: мемориал, могилу MLK и Coretta Scott King, вечный огонь и музей движения", "type": "explore", "target": "king_center", "count": 4, "optional": false}, {"id": "listen_dream_speech", "text": "Прослушать запись речи «I Have a Dream», активировав эмоциональный триггер", "type": "interact", "target": "i_have_a_dream_speech", "count": 1, "optional": false}, {"id": "complete_reflection", "text": "Завершить квест, осознавая борьбу за гражданские права и текущие социальные вызовы", "type": "interact", "target": "civil_rights_reflection", "count": 1, "optional": false}], "rewards": {"experience": 5000, "money": 0, "items": [{"item_id": "achievement_dreamer", "quantity": 1}], "reputation": {}, "unlocks": ["civil_rights_quests", "social_justice_access"]}, "branches": [{"branch_id": "embrace_dream", "description": "Принять идеалы мечты", "consequences": {"skills": {"social_justice": 35, "empathy": 25}, "reputation": {"Civil_Rights_Movement": 30}, "unlocks": ["advanced_social_justice_quests"]}}]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "Исторический парк Мартина Лютера Кинга в Атланте фиксирует начало жизни лидера движения за гражданские права и ключевые точки его служения.\\nКвест воспроизводит маршрут по основным объектам, формируя эмоциональное и образовательное воздействие.\\n", "mechanics_links": [], "assets": []}, {"id": "quest-steps", "title": "Этапы", "body": "1. Посетить дом рождения MLK на Auburn Avenue и изучить экспозицию 1929 года.\\n2. Отправиться в церковь Ebenezer Baptist Church, где лидер проповедовал и обучал.\\n3. Исследовать King Center: мемориал, могилу MLK и Coretta Scott King, вечный огонь и музей движения.\\n4. Прослушать запись речи «I Have a Dream», активировав эмоциональный триггер.\\n5. Завершить квест, осознавая борьбу за гражданские права и текущие социальные вызовы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional-details", "title": "Региональные детали", "body": "Квест подчёркивает, что MLK родился в Атланте в 1929 году, служил в Ebenezer Baptist Church и был убит в 1968 году.\\nМемориал и вечный огонь становятся центральными символами противостояния несправедливости.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Опыт: 5000 XP.\\nВалюта: 0.\\nСоциальная справедливость: +30.\\nАчивка: «Мечтатель».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Исторические и мемориальные механики дополняются эмоциональными триггерами, влияющими на социальные системы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 33, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики исторического квеста, движения за гражданские права и социальной справедливости."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста MLK Historic Site в YAML и описание маршрута по историческим точкам."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

