-- Quest: canon-quest-ny-one-world-trade
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-one-world-trade', 'Нью-Йорк — One World Trade', 'Игрок поднимается на 102-й этаж, наблюдает панораму, отдаёт дань памяти и завершает сцену закатом над восстановленным городом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-one-world-trade", "title": "Нью-Йорк — One World Trade", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.2.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:26:30+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "memorial", "tourism"], "topics": ["remembrance", "tourism", "resilience"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "culture-service", "wellbeing-service", "legacy-system"], "related_documents": [{"id": "canon-quest-ny-911-memorial", "relation": "complements"}, {"id": "github-issue-360", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/360", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2078-2093/quest-048-one-world-trade.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристический квест One World Trade не фиксировал эмоциональные эффекты и поэтапный маршрут.", "goal": "Описать подъём на Freedom Tower, мемориал и вдохновляющий эффект для игроков.", "essence": "Игрок поднимается на 102-й этаж, наблюдает панораму, отдаёт дань памяти и завершает сцену закатом над восстановленным городом.", "key_points": ["Расписаны факты о высоте, символике 1,776 футов и слогане «Never forget».", "Этапы включают смотровую площадку, мемориальный уровень и рефлексию.", "Награда — вдохновение и achievement «На Вершине Свободы»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: medium, формат: solo, длительность: 1-2 часа.\\nНаграды: 1,500 XP, -40 едди (билет), +10% к вдохновению на 24 часа, ачивка «На Вершине Свободы».\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Подняться на 102-й этаж Freedom Tower и активировать панорамный обзор.\\n2. Осмотреть экспозицию мемориального уровня Ground Zero.\\n3. Провести рефлексию в зале памяти, прослушать аудиогид о событии 9/11.\\n4. Встретить закат на смотровой площадке и сделать фото skyline.\\n", "mechanics_links": ["mechanics/world/world-map.yaml", "mechanics/narrative/reference-usage.yaml"], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест повышает вдохновение персонажа, открывает скидки на туристические маршруты и мемориальные товары.\\nУчастие добавляет снимок в персональную хронику и влияет на события remembrance-week.\\nОтказ снижает доверие Memorial Foundation и закрывает доступ к эксклюзивным экскурсиям.\\n", "mechanics_links": ["mechanics/world/mood-system.yaml", "mechanics/economy/resource-pipelines.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "One World Trade Center Facts", "link": "https://onewtc.com"}, {"title": "9/11 Memorial & Museum", "link": "https://www.911memorial.org"}], "decisions": []}, "implementation": {"github_issue": 360, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.2.0", "date": "2025-12-12", "author": "content_team", "changes": "Добавлены ссылки на механики (quest, reputation, world-map, narrative reference, mood/economy), обновлена дата."}, {"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Документ обновлён до шаблона 1.1.0, добавлены системные связи и этапы."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Первичный перенос в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

