INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-goodbye', 'Нью-Йорк — Прощай, Нью-Йорк', 'Игрок проживает утро, день, вечер и ночь в любимых локациях, прощаясь с городом перед перезапуском.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-goodbye", "title": "Нью-Йорк — Прощай, Нью-Йорк", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.2.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:27:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "farewell", "tour"], "topics": ["atmosphere", "nostalgia", "closure"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "social-service", "culture-service", "legacy-system", "wellbeing-service"], "related_documents": [{"id": "github-issue-360", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/360", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2078-2093/quest-049-goodbye-new-york.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Прощальный тур Нью-Йорка не фиксировал временные слоты и эмоциональные эффекты.", "goal": "Сформировать YAML-описание последнего дня, связывая культовые места с эмоциональными состояниями.", "essence": "Игрок проживает утро, день, вечер и ночь в любимых локациях, прощаясь с городом перед перезапуском.", "key_points": ["Структурирован маршрут по времени суток и ключевым местам.", "Зафиксированы атрибуты Нью-Йорка (желтые такси, skyline, hustle and bustle).", "Награда — ачивка «Нью-Йоркское Сердце» и эмоциональный бафф."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 1-2 часа.\\nНаграды: 2,000 XP, бафф «Прощание с городом» (+эмпатия в социальных сценах), ачивка «Нью-Йоркское Сердце».\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}, {"id": "stages", "title": "Маршрут дня", "body": "1. Утро — завтрак в пиццерии: вспоминаем начало кампании.\\n2. День — прогулка по Центральному парку, фото у Bethesda Terrace.\\n3. Вечер — закат на Бруклинском мосту, наблюдение skyline.\\n4. Ночь — огни Таймс-сквер, последнее «I ♥ NY».\\n5. Рассвет — паром к Статуе Свободы и финальное прощание.\\n", "mechanics_links": ["mechanics/world/world-map.yaml", "mechanics/narrative/branching-decisions.yaml"], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест обновляет mood-system, выдавая бафф ностальгии и открывая финальные диалоги с NPC.\\nПрохождение добавляет снимки маршрута в персональный архив и влияет на farewell-события города.\\nПропуск снижает впечатление сезона и закрывает часть эпилога.\\n", "mechanics_links": ["mechanics/world/mood-system.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "NYC Farewell Tours", "link": "https://www.timeout.com/newyork"}, {"title": "New York State of Mind History", "link": "https://www.billyjoel.com"}], "decisions": []}, "implementation": {"github_issue": 360, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.2.0", "date": "2025-12-12", "author": "content_team", "changes": "Добавлены механики (quest, reputation, world-map, branching, mood), обновлена дата."}, {"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Маршрут и системные эффекты переработаны под шаблон 1.1.0."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Первичный YAML-перенос прощального квеста."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

