INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-atlanta-2020-2029-hip-hop-capital', 'Атланта 2020-2029 — Столица хип-хопа', 'Игрок слушает легендарные треки, посещает студии и клубы, погружаясь в атмосферу «Hollywood of the South».', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "listen_atl_tracks", "text": "Прослушать ключевые треки ATL: OutKast «ATLiens», Migos «Bad and Boujee», Lil Baby и других артистов", "type": "interact", "target": "atlanta_hip_hop_tracks", "count": 3, "optional": false}, {"id": "visit_patchwerk_studio", "text": "Посетить студию Patchwerk или эквивалент, где записываются знаменитые альбомы", "type": "interact", "target": "patchwerk_studio", "count": 1, "optional": false}, {"id": "visit_magic_city", "text": "Заглянуть в Magic City или другой культовый клуб, ощутить атмосферу сцены", "type": "interact", "target": "magic_city_club", "count": 1, "optional": false}, {"id": "participate_rap_battle", "text": "Участвовать или наблюдать за freestyle rap battle", "type": "interact", "target": "rap_battle", "count": 1, "optional": false}, {"id": "realize_hip_hop_mecca", "text": "Осознать роль Атланты как новой Мекки хип-хопа и центра trap музыки", "type": "interact", "target": "hip_hop_mecca_reflection", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -40, "items": [{"item_id": "achievement_atl_rapper", "quantity": 1}], "reputation": {}, "unlocks": ["music_quests", "hip_hop_culture_access"]}'::jsonb, '[{"branch_id": "trap_enthusiast", "description": "Стать энтузиастом trap музыки", "consequences": {"skills": {"culture": 25, "music": 20}, "reputation": {"Hip_Hop_Community": 25}, "unlocks": ["exclusive_hip_hop_access"]}}]'::jsonb, '{"metadata": {"id": "canon-quest-atlanta-2020-2029-hip-hop-capit