-- Quest: canon-quest-ny-american-legacy
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-american-legacy', 'Нью-Йорк — Американское наследие', 'Игрок вступает в Sons of Liberty, выполняет патриотические поручения и накапливает Legacy-пойнты перед новой лигой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-american-legacy", "title": "Нью-Йорк — Американское наследие", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T15:22:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:22:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "sons_of_liberty", "legacy"], "topics": ["faction-progress", "legacy-system", "patriotic-narrative"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "faction-service", "legend-service", "legacy-system", "reputation-service"], "related_documents": [{"id": "canon-quest-ny-league-constitution", "relation": "complements"}, {"id": "github-issue-358", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/358", "relation": "migrated_to", "migrated_at": "2025-11-22T20:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2078-2093/quest-043-american-legacy.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гильдейская линия Sons of Liberty была в Markdown и не интегрировалась с системой Legacy.", "goal": "Структурировать американский финальный квест в YAML, отметив гильдейские этапы, рейтинги и патриотические события.", "essence": "Игрок вступает в Sons of Liberty, выполняет патриотические поручения и накапливает Legacy-пойнты перед новой лигой.", "key_points": ["Обозначены требования к гильдии, три ключевые миссии и финал с зачислением в таблицу Legacy.", "Награды включают XP, гильдейские бонусы и титул «Сын Свободы».", "Встроены связи с фракциями города и сезонными событиями финальной лиги."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main, сложность: medium, формат: группа 5-10 игроков, длительность: 2-4 часа.\\nНаграды: 5,000 XP, титул «Сын Свободы», +15 к репутации Sons of Liberty, Legacy-бонус уровня 2.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Пройти испытания вступления и принести клятву Sons of Liberty.\\n2. Выполнить патриотические поручения (защита граждан, контрпропаганда, акции неповиновения корпорациям).\\n3. Провести публичное выступление на площади Union Square и привлечь союзников.\\n4. Завершить сезонный рейд против корпоративного конвоя, собирая Legacy-очки.\\n5. Сдать отчёт и зафиксировать результат гильдии в таблице Legacy перед перезапуском лиги.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Нью-Йорк 2078-2093 переживает волны патриотических движений и протестов против корпораций.\\nSons of Liberty используют исторические символы города и организуют акции на Бруклинском мосту, Таймс-сквер и Манхэттене.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест усиливает систему Legacy: гильдия получает периодические бонусы и изменяет доступ к сезонным наградам.\\nРешения игрока влияют на репутацию фракций города и открывают дополнительные задания в финальных событиях Нью-Йорка.\\nПровал миссий снижает рейтинг Sons of Liberty и активирует ответные меры корпораций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Sons of Liberty Historical Inspiration", "link": "https://www.history.com"}, {"title": "NYC Protest Timeline 2070-2090", "link": "https://archive.necp.game"}], "decisions": []}, "implementation": {"github_issue": 358, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-ny-american-legacy"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Расширены этапы, награды и связи с системой Legacy."}, {"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Американское Наследие» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

