INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-phoenix-sedona-red-rocks', 'Феникс 2020-2029 — Красные скалы Седоны', 'Игрок отправляется из Феникса в Седону, исследует красные скалы и встречает практики New Age в «энергетических вихрях».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-phoenix-sedona-red-rocks", "title": "Феникс 2020-2029 — Красные скалы Седоны", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["phoenix", "sedona", "spirituality"], "topics": ["exploration", "spiritual-tourism"], "related_systems": ["narrative-service", "exploration-system", "buff-system"], "related_documents": [{"id": "github-issue-363", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/363", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/phoenix/2020-2029/quest-008-sedona-red-rocks.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Путевой квест о Седоне существовал в Markdown и не проходил валидацию знаний.", "goal": "Сформировать YAML-описание поездки в Sedona с природными точками и духовными активностями.", "essence": "Игрок отправляется из Феникса в Седону, исследует красные скалы и встречает практики New Age в «энергетических вихрях».", "key_points": ["Квест-id PHOENIX-2029-008, сложность medium, соло-прохождение 4+ часов.", {"Маршрут описывает ключевые точки": "Cathedral Rock, Bell Rock и духовные практики."}, "Награды дают 2 500 XP, -40 валюты и +20 к духовному ресурсу вместе с ачивкой «Искатель Вихрей»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест раскрывает феномен красных скал Седоны, сочетая природный туризм, хайкинг и магические верования New Age о вихрях энергии.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Организовать выезд из Феникса и прибыть в Sedona после двухчасовой дороги.\\n2. Посетить культовые формации Cathedral Rock и Bell Rock, записав визуальные этюды для контента.\\n3. Взаимодействовать с практиками на энергетических вихрях, проходя мини-испытания на созерцание.\\n4. Совершить хайкинг по маршрутам среди красных скал и вернуться с духовными инсайтами.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны марсианские пейзажи, культовые тропы, альтернативные целители и магазины New Age, а также 180 км расстояния от Феникса.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 2 500 XP, тратит 40 едди на поездку и получает +20 к духовному ресурсу и титул «Искатель Вихрей».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 363, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-phoenix-sedona-red-rocks"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Красные скалы Седоны» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_