-- Quest: canon-quest-phoenix-urban-sprawl
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-phoenix-urban-sprawl', 'Феникс 2020-2029 — Городская экспансия', 'Игрок наблюдает бесконечное расползание пригородов Феникса по пустыне и анализирует угрозы, связанные с водой и автозависимостью.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-phoenix-urban-sprawl", "title": "Феникс 2020-2029 — Городская экспансия", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["phoenix", "urbanism", "infrastructure"], "topics": ["city-growth", "environment-impact"], "related_systems": ["narrative-service", "city-simulation", "environment-system"], "related_documents": [{"id": "github-issue-363", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/363", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/phoenix/2020-2029/quest-010-urban-sprawl.md", "visibility": "internal", "audience": ["narrative", "vision", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о городской экспансии Феникса существовал в Markdown со статусом draft и отсутствовал в проверяемой базе знаний.", "goal": "Сформировать структурированный YAML о феномене urban sprawl с привязкой к устойчивости, транспорту и воде.", "essence": "Игрок наблюдает бесконечное расползание пригородов Феникса по пустыне и анализирует угрозы, связанные с водой и автозависимостью.", "key_points": ["Квест-id PHOENIX-2029-010, сложность medium, соло-формат на 2–4 часа.", "Этапы показывают обзор с воздуха, однообразные пригороды, гигантские шоссе и финальную оценку устойчивости.", "Награды дают 2 000 XP, +20 к урбанистическому ресурсу и титул «Свидетель Экспансии»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест исследует urban sprawl Феникса: отсутствие общественного транспорта, бесконечный рост пригородов и рискованную зависимость от\\nавтомобильной инфраструктуры в условиях пустыни.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Провести аэрофотосъёмку и увидеть масштаб расползающихся пригородов.\\n2. Исследовать однотипные жилые зоны и их потребление воды.\\n3. Проанализировать транспортные узлы с 10-полосными шоссе и отсутствие альтернатив.\\n4. Сопоставить рост города с ограниченными водными ресурсами и сформулировать выводы для городского планирования.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указано, что город в пустыне опирается на автомобиль как основной вид транспорта, пригороды единообразны, а эко-система страдает из-за\\nпотребления воды и поглощения пустыни.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест выдаёт 2 000 XP, сохраняет баланс валюты и добавляет +20 к урбанистическому ресурсу вместе с титулом «Свидетель Экспансии».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 363, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-phoenix-urban-sprawl"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Городская экспансия» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

