INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-boston-2020-2029-freedom-trail', 'Бостон 2020-2029 — Тропа Свободы', 'Игрок следует по красной линии через ключевые места Американской революции, ощущая рождение страны.', 'main', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "start_boston_common", "text": "Начать путь в Boston Common и получить карту тропы", "type": "interact", "target": "boston_common", "count": 1, "optional": false}, {"id": "visit_state_house", "text": "Посетить Massachusetts State House, Park Street Church и Granary Burying Ground", "type": "explore", "target": "freedom_trail_points", "count": 3, "optional": false}, {"id": "explore_meeting_houses", "text": "Исследовать Old South Meeting House, Old State House и место Бостонской бойни", "type": "explore", "target": "revolutionary_sites", "count": 3, "optional": false}, {"id": "visit_faneuil_hall", "text": "Продолжить к Faneuil Hall, дому Пола Ревира и Old North Church", "type": "explore", "target": "faneuil_revere_north", "count": 3, "optional": false}, {"id": "complete_constitution", "text": "Завершить маршрут у USS Constitution и музея, закрепив знания об Американской революции", "type": "interact", "target": "uss_constitution", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "money": 0, "items": [{"item_id": "achievement_revolutionary_patriot", "quantity": 1}], "reputation": {}, "unlocks": ["historical_quests", "revolutionary_heritage"]}'::jsonb, '[{"branch_id": "deep_historical_knowledge", "description": "Глубокое изучение истории революции", "consequences": {"skills": {"history": 35, "education": 20}, "reputation": {"Historical_Societies": 25}, "unlocks": ["advanced_historical_quests"]}}]'::jsonb, '{"metadata": {"id": "canon-quest-boston-2020-2029-freedom-trail", "title": "Бостон 2020-2029 — Тропа Свободы", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["america", "boston", "quest"], "topics": ["timeline-author", "revolution"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service"], "related_documents": [{"id": "canon-lore-america-boston-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/boston/2020-2029/quest-001-freedom-trail.md", "visibility": "internal", "audience": ["lore", "narrative", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест по Freedom Trail оставался в Markdown и не отражал полноту революционного маршрута.", "goal": "Зафиксировать 16 точек исторической тропы в YAML и интегрировать их в лор Бостона.", "essence": "Игрок следует по красной линии через ключевые места Американской революции, ощущая рождение страны.", "key_points": ["Маршрут стартует в Boston Common и охватывает 16 исторических объектов.", "Faneuil Hall, Paul Revere House и USS Constitution подчёркивают роль города.", "Квест даёт образовательный бонус и усиливает патриотическую линию."]}, "quest_definition": {"quest_type": "main", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "start_boston_common", "text": "Начать путь в Boston Common и получить карту тропы", "type": "interact", "target": "boston_common", "count": 1, "optional": false}, {"id": "visit_state_house", "text": "Посетить Massachusetts State House, Park Street Church и Granary Burying Ground", "type": "explore", "target": "freedom_trail_points", "count": 3, "optional": false}, {"id": "explore_meeting_houses", "text": "Исследовать Old South Meeting House, Old State House и место Бостонской бойни", "type": "explore", "target": "revolutionary_sites", "count": 3, "optional": false}, {"id": "visit_faneuil_hall", "text": "Продолжить к Faneuil Hall, дому Пола Ревира и Old North Church", "type": "explore", "target": "faneuil_revere_north", "count": 3, "optional": false}, {"id": "complete_constitution", "text": "Завершить маршрут у USS Constitution и музея, закрепив знания об Американской революции", "type": "interact", "target": "uss_constitution", "count": 1, "optional": false}], "rewards": {"experience": 5000, "money": 0, "items": [{"item_id": "achievement_revolutionary_patriot", "quantity": 1}], "reputation": {}, "unlocks": ["historical_quests", "revolutionary_heritage"]}, "branches": [{"branch_id": "deep_historical_knowledge", "description": "Глубокое изучение истории революции", "consequences": {"skills": {"history": 35, "education": 20}, "reputation": {"Historical_Societies": 25}, "unlocks": ["advanced_historical_quests"]}}]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "Freedom Trail — 4-километровый маршрут по красной линии, объединяющий центры революционной истории.\\nКвест подчёркивает, что Бостон — «колыбель свободы».\\n", "mechanics_links": [], "assets": []}, {"id": "quest-steps", "title": "Этапы", "body": "1. Начать путь в Boston Common и получить карту тропы.\\n2. Посетить Massachusetts State House, Park Street Church и Granary Burying Ground.\\n3. Исследовать Old South Meeting House, Old State House и место Бостонской бойни.\\n4. Продолжить к Faneuil Hall, дому Пола Ревира и Old North Church.\\n5. Завершить маршрут у USS Constitution и музея, закрепив знания об Американской революции.\\n", "mechanics_links": [], "assets": []}, {"id": "regional-details", "title": "Региональные детали", "body": "Красная линия, могилы отцов-основателей, события 1775-1783 и статус Бостона как центр революции.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Опыт: 5000 XP.\\nВалюта: 0.\\nИстория: +30.\\nАчивка: «Патриот революции».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Исторические, туристические и образовательные механики, раскрывающие Американскую революцию.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 32, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики исторического квеста, Американской революции и патриотической линии."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Тропа Свободы» в YAML и описание маршрута по ключевым точкам."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

