-- Quest: canon-quest-ny-final-pizza
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-final-pizza', 'Нью-Йорк — Последняя пицца', 'Игрок возвращается в любимую пиццерию, ест последний кусок перед перезапуском лиги и закрывает круг истории.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-final-pizza", "title": "Нью-Йорк — Последняя пицца", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.2.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:24:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "pizza", "nostalgia"], "topics": ["regional-content", "lifestyle", "closure"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "wellbeing-service", "legacy-system"], "related_documents": [{"id": "canon-quest-ny-pizza-rat", "relation": "bookend"}, {"id": "github-issue-360", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/360", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2078-2093/quest-047-final-pizza.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Эмоциональное прощание с Нью-Йоркской пиццей существовало в Markdown и не влияло на системы настроения.", "goal": "Описать квест в YAML, подчеркнув ностальгию, связь с началом кампании и бонусы комфорту.", "essence": "Игрок возвращается в любимую пиццерию, ест последний кусок перед перезапуском лиги и закрывает круг истории.", "key_points": ["Прописаны этапы визита, дегустации и прощания с пиццмейкером.", "Награда — эмоциональный бафф и ачивка.", "Квест служит эпилогом линии городских вкусов."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 30-60 минут.\\nНаграды: 800 XP, -3 едди, бафф «Ностальгия» (повышает настроение и лояльность к местной кухне), ачивка «Душа Пиццы».\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Вернуться в пиццерию, где начиналась история персонажа.\\n2. Заказать фирменный кусок и вспомнить прошлые события.\\n3. Сложить кусок пополам и съесть по классическому способу.\\n4. Поговорить с пиццмейкером, услышать его историю о конце лиги.\\n5. Оставить чаевые и сфотографироваться, зафиксировав момент для архива памяти.\\n", "mechanics_links": ["mechanics/narrative/branching-decisions.yaml", "mechanics/world/offline-networks.yaml"], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест активирует систему воспоминаний: фото добавляется в персональную хронику.\\nБафф «Ностальгия» повышает эффективность социальной игры в Нью-Йорке на следующий день.\\nСвязь с culinary-system открывает новые рецепты и скидки в городских заведениях.\\n", "mechanics_links": ["mechanics/world/mood-system.yaml", "mechanics/economy/resource-pipelines.yaml", "mechanics/world/world-map.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "NYC Pizza Culture", "link": "https://www.eater.com"}, {"title": "Slice of Life Documentary", "link": "https://www.netflix.com"}], "decisions": []}, "implementation": {"github_issue": 360, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.2.0", "date": "2025-12-12", "author": "content_team", "changes": "Добавлены ссылки на механики (quest, reputation, branching, offline-networks, mood/economy/world-map), обновлена дата."}, {"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Расширены эмоциональные элементы и введён бафф «Ностальгия»."}, {"version": "0.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Исходный YAML-перенос прощальной пиццы."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

