INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-philadelphia-reading-terminal', 'Филадельфия 2020-2029 — «Рынок Ридинг Терминал»', 'Игрок дегустирует фирменные блюда, взаимодействует с продавцами амишей и открывает торговые контакты Филадельфии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-philadelphia-reading-terminal", "title": "Филадельфия 2020-2029 — «Рынок Ридинг Терминал»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["philadelphia", "market", "culinary"], "topics": ["social-experience", "food-culture"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-region-america-philadelphia-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/philadelphia/2020-2029/quest-007-reading-terminal.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание не фиксировало конкретных точек рынка, взаимодействие с амишей и экономические бонусы.", "goal": "Создать маршрут по Reading Terminal Market с фокусом на кулинарные активности и социальную вовлечённость.", "essence": "Игрок дегустирует фирменные блюда, взаимодействует с продавцами амишей и открывает торговые контакты Филадельфии.", "key_points": ["Исторический рынок 1893 года с отдельным блоком для Pennsylvania Dutch.", "Механики дегустации и торговли повышают параметр «Еда» и социальную репутацию.", "На выходе игрок получает контакт для поставок ингредиентов."]}, "content": {"sections": [{"id": "welcome", "title": "Навигация по рынку", "body": "NPC-гид встречает игрока у главного входа и выдает карту с отмеченными точками: Beiler''s Donuts, стенды амишей,\\nприлавок Philly cheesesteak и свежие фермерские продукты.\\n", "mechanics_links": [], "assets": []}, {"id": "tastings", "title": "Дегустации и социальные проверки", "body": "- Мини-игра «Пончик амишей»: удержать нужную температуру глазури, чтобы получить бафф к «Еде».\\n- Диалог с фермером Pennsylvania Dutch открывает скидку и скрытый рецепт.\\n- Короткая сцена с очередью за cheesesteak проверяет навыки коммуникации.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 800 XP, -30 кредов (покупки) и +10 к параметру «Еда».\\n- Ачивка «Торговец Рынка», открывающая цепочку локальных поставщиков.\\n- Контракт «Dutch Supplies» для кулинарных событий.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Pennsylvania Dutch", "definition": "Сообщество амишей и меннонитов, сохраняющих традиционные методы фермерства и кулинарии."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Рынок Ридинг Терминал» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

