INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-boston-2029-clam-chowder', 'Бостон 2020-2029 — Суп Клэм Чаудер', 'Игрок пробует фирменный бостонский суп и знакомится с местными традициями и соперничеством Нью-Ингленда и Манхэттена.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_seafood_restaurant", "text": "Найти заведении морепродуктов в City Center", "type": "interact", "target": "seafood_restaurant", "count": 1, "optional": false}, {"id": "order_chowder_bread_bowl", "text": "Заказать суп в хлебной миске", "type": "interact", "target": "chowder_order", "count": 1, "optional": false}, {"id": "evaluate_ingredients", "text": "Оценить ингредиенты: моллюски, картофель, лук, сливки", "type": "interact", "target": "chowder_evaluation", "count": 1, "optional": false}, {"id": "eat_bread_bowl", "text": "Съесть пропитавшийся супом хлеб", "type": "interact", "target": "bread_bowl_consumption", "count": 1, "optional": false}, {"id": "learn_unspoken_rule", "text": "Усвоить негласное правило — не хвалить манхэттенский вариант", "type": "interact", "target": "chowder_etiquette", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "money": -20, "items": [{"item_id": "achievement_chowder_gourmet", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "new_england_cuisine_access"]}'::jsonb, '[{"branch_id": "new_england_food_expert", "description": "Стать знатоком New England кухни", "consequences": {"skills": {"culinary": 20}, "reputation": {"Seafood_Restaurants": 15}, "unlocks": ["exclusive_seafood_access"]}}]'::jsonb, '{"metadata": {"id": "quest-boston-2029-clam-chowder", "title": "Бостон 2020-2029 — Суп Клэм Чаудер", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["boston", "cuisine", "social"], "topics": ["author-events", "city-life"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1204", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1204", "relation": "migrated_to", "migrated_at": "2025-11-23T03:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/boston/2020-2029/quest-005-clam-chowder.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарный квест о New England clam chowder хранился в Markdown без структурированной информации.", "goal": "Перенести квест в формат знаний, подчеркнув культурные особенности и игровые награды.", "essence": "Игрок пробует фирменный бостонский суп и знакомится с местными традициями и соперничеством Нью-Ингленда и Манхэттена.", "key_points": ["Квест сосредоточен на ресторане морепродуктов и подаче супа в хлебной миске.", "Выделяет региональную гордость за белый сливочный chowder и конкуренцию с манхэттенским вариантом.", "Предоставляет пищевой бафф и тематическую ачивку."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_seafood_restaurant", "text": "Найти заведении морепродуктов в City Center", "type": "interact", "target": "seafood_restaurant", "count": 1, "optional": false}, {"id": "order_chowder_bread_bowl", "text": "Заказать суп в хлебной миске", "type": "interact", "target": "chowder_order", "count": 1, "optional": false}, {"id": "evaluate_ingredients", "text": "Оценить ингредиенты: моллюски, картофель, лук, сливки", "type": "interact", "target": "chowder_evaluation", "count": 1, "optional": false}, {"id": "eat_bread_bowl", "text": "Съесть пропитавшийся супом хлеб", "type": "interact", "target": "bread_bowl_consumption", "count": 1, "optional": false}, {"id": "learn_unspoken_rule", "text": "Усвоить негласное правило — не хвалить манхэттенский вариант", "type": "interact", "target": "chowder_etiquette", "count": 1, "optional": false}], "rewards": {"experience": 600, "money": -20, "items": [{"item_id": "achievement_chowder_gourmet", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "new_england_cuisine_access"]}, "branches": [{"branch_id": "new_england_food_expert", "description": "Стать знатоком New England кухни", "consequences": {"skills": {"culinary": 20}, "reputation": {"Seafood_Restaurants": 15}, "unlocks": ["exclusive_seafood_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает посетить культовый ресторан морепродуктов (например, Legal Sea Foods), заказать сливочный New\\nEngland clam chowder и полностью погрузиться в гастрономическую традицию Бостона.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Найти заведении морепродуктов в City Center.\\n2. Заказать суп в хлебной миске.\\n3. Оценить ингредиенты: моллюски, картофель, лук, сливки.\\n4. Съесть пропитавшийся супом хлеб.\\n5. Усвоить негласное правило — не хвалить манхэттенский вариант.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Бостонский chowder — белый и сливочный, подаётся в съедобной хлебной миске. Кухонная гордость региона подчёркивает\\nпротивостояние с Нью-Йорком, где предпочитают томатный суп.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 600 XP\\n- Расходы: 20 единиц валюты\\n- Бафф: +20% HP на 6 часов\\n- Ачивка: «Чаудер-Гурман»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест раскрывает кулинарный слой города, вводит локальный сленг и усиливает социальные взаимодействия в Бостоне.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Относится к кулинарной линии Бостона и может дополнять другие «slice of life» сценарии региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1204, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики кулинарного квеста, New England кухни и региональной гордости."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест о New England clam chowder в структурированный формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

