INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-sf-fishermans-wharf', 'Сан-Франциско — Причал Рыбаков', 'Игрок изучает причал Рыбаков, взаимодействует с морскими львами, дегустирует clam chowder и собирает эстетические бонусы.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_wharf", "text": "Добраться до Fisherman''s Wharf и активировать туристическую метку", "type": "interact", "target": "fishermans_wharf_location", "count": 1, "optional": false}, {"id": "observe_sea_lions", "text": "Наблюдать за морскими львами на понтонах, фиксируя редкие крики в журнале", "type": "interact", "target": "sea_lions_observation", "count": 1, "optional": false}, {"id": "visit_pier_39", "text": "Отправиться на Pier 39, посетить магазины и мини-аттракционы", "type": "interact", "target": "pier_39_exploration", "count": 1, "optional": false}, {"id": "try_clam_chowder", "text": "Заказать clam chowder в хлебной миске и получить временный бафф", "type": "interact", "target": "clam_chowder_tasting", "count": 1, "optional": false}, {"id": "buy_souvenir", "text": "Купить уникальный сувенир и открыть ветку гастротуризма", "type": "interact", "target": "wharf_souvenir", "count": 1, "optional": false}]'::jsonb, '{"xp": 600, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "sea_lions_friend", "name": "Друг Морских Львов"}], "skill_boosts": {"health_temp": 20}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-sf-fishermans-wharf", "title": "Сан-Франциско — Причал Рыбаков", "document_type": "canon", "category": "quest", "status": "approved", "version": "2.0.0", "last_updated": "2025-11-29T16:38:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T16:38:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["san_francisco", "fishermans_wharf", "sea_lions"], "topics": ["tourism", "culinary", "urban-landmarks"], "related_systems": ["tourism-system", "economy-system", "buff-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/san-francisco/2020-2029/quest-005-fishermans-wharf.md", "visibility": "internal", "audience": ["narrative", "content", "vision"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест Fisherman''s Wharf существовал в Markdown и не описывал систему баффов и туристических наград.", "goal": "Структурировать туристический маршрут с кулинарными и атмосферными бонусами.", "essence": "Игрок изучает причал Рыбаков, взаимодействует с морскими львами, дегустирует clam chowder и собирает эстетические бонусы.", "key_points": ["Расписаны этапы посещения причала, Pier 39 и дегустации блюд.", "Добавлены системные связи для туризма, экономики и баффов здоровья.", "Определены награды, титул и последствия пропуска ключевых активностей."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_wharf", "text": "Добраться до Fisherman''s Wharf и активировать туристическую метку", "type": "interact", "target": "fishermans_wharf_location", "count": 1, "optional": false}, {"id": "observe_sea_lions", "text": "Наблюдать за морскими львами на понтонах, фиксируя редкие крики в журнале", "type": "interact", "target": "sea_lions_observation", "count": 1, "optional": false}, {"id": "visit_pier_39", "text": "Отправиться на Pier 39, посетить магазины и мини-аттракционы", "type": "interact", "target": "pier_39_exploration", "count": 1, "optional": false}, {"id": "try_clam_chowder", "text": "Заказать clam chowder в хлебной миске и получить временный бафф", "type": "interact", "target": "clam_chowder_tasting", "count": 1, "optional": false}, {"id": "buy_souvenir", "text": "Купить уникальный сувенир и открыть ветку гастротуризма", "type": "interact", "target": "wharf_souvenir", "count": 1, "optional": false}], "rewards": {"xp": 600, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "sea_lions_friend", "name": "Друг Морских Львов"}], "skill_boosts": {"health_temp": 20}}, "branches": []}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 600 XP, бафф +20% HP на 8 часов, титул «Друг Морских Львов».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Fisherman''s Wharf и активировать туристическую метку.\\n2. Наблюдать за морскими львами на понтонах, фиксируя редкие крики в журнале.\\n3. Отправиться на Pier 39, посетить магазины и мини-аттракционы.\\n4. Заказать clam chowder в хлебной миске и получить временный бафф.\\n5. Купить уникальный сувенир и открыть ветку гастротуризма.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "tourism-system регистрирует достопримечательности и выдаёт очки опыта путешественника.\\neconomy-system учитывает траты и скидки, buff-system назначает временные бонусы к здоровью.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны запах моря, уличные музыканты, sourdough bread bowls и шумные толпы.\\nВключены вид на мост Золотые Ворота, заходы кораблей и мерцающие вывески причала.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успешное прохождение открывает скидки у уличных торговцев и повышает привлекательность региона.\\nПропуск дегустации уменьшает длительность баффа и закрывает доступ к секретному рецепту.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Fisherman''s Wharf Visitor Guide", "link": "https://www.fishermanswharf.org"}, {"title": "Pier 39 Sea Lions Live Cam", "link": "https://www.pier39.com"}], "decisions": []}, "implementation": {"github_issue": 248, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Структурированы туристические активности и баффы причала Рыбаков."}, {"version": "0.1.0", "date": "2025-11-09", "author": "narrative_team", "changes": "Markdown-описание похода на Fisherman''s Wharf."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

