INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-boston-2029-samuel-adams', 'Бостон 2020-2029 — Пиво Сэмюэл Адамс', 'Игрок посещает пивоварню Boston Beer Company, узнаёт историю Сэмюэла Адамса и дегустирует культовые сорта.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_brewery", "text": "Добраться до пивоварни Boston Beer Company в Jamaica Plain", "type": "interact", "target": "boston_beer_company", "count": 1, "optional": false}, {"id": "listen_brand_history", "text": "Прослушать историю бренда и связь с Сэмюэлом Адамсом", "type": "interact", "target": "brand_history", "count": 1, "optional": false}, {"id": "study_production", "text": "Изучить этапы производства и оборудование", "type": "explore", "target": "brewery_production", "count": 1, "optional": false}, {"id": "taste_beers", "text": "Провести дегустацию Boston Lager и сезонных сортов", "type": "interact", "target": "beer_tasting", "count": 2, "optional": false}, {"id": "purchase_products", "text": "Приобрести продукцию или мерч для дальнейшего использования", "type": "interact", "target": "brewery_shop", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "money": -30, "items": [{"item_id": "achievement_brewer_patriot", "quantity": 1}], "reputation": {}, "unlocks": ["craft_beer_quests", "brewery_access"]}'::jsonb, '[{"branch_id": "craft_beer_enthusiast", "description": "Стать энтузиастом craft beer", "consequences": {"skills": {"charisma": 20, "culture": 15}, "reputation": {"Craft_Beer_Community": 20}, "unlocks": ["exclusive_brewery_access"]}}]'::jsonb, '{"metadata": {"id": "quest-boston-2029-samuel-adams", "title": "Бостон 2020-2029 — Пиво Сэмюэл Адамс", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["boston", "craft-beer", "social"], "topics": ["author-events", "city-life"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1207", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1207", "relation": "migrated_to", "migrated_at": "2025-11-23T03:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/boston/2020-2029/quest-008-samuel-adams-beer.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Тур по пивоварне Samuel Adams был описан в Markdown и не интегрирован в базу знаний.", "goal": "Формализовать квест о ремесленном пиве Бостона с историческими ссылками и игровыми наградами.", "essence": "Игрок посещает пивоварню Boston Beer Company, узнаёт историю Сэмюэла Адамса и дегустирует культовые сорта.", "key_points": ["Тур по производству в районе Jamaica Plain с историей основания в 1984 году.", "Дегустация Boston Lager и сезонных сортов, подчёркивающих craft beer революцию.", "Квест связывает алкогольно-культурную линию города с наследием отца-основателя."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_brewery", "text": "Добраться до пивоварни Boston Beer Company в Jamaica Plain", "type": "interact", "target": "boston_beer_company", "count": 1, "optional": false}, {"id": "listen_brand_history", "text": "Прослушать историю бренда и связь с Сэмюэлом Адамсом", "type": "interact", "target": "brand_history", "count": 1, "optional": false}, {"id": "study_production", "text": "Изучить этапы производства и оборудование", "type": "explore", "target": "brewery_production", "count": 1, "optional": false}, {"id": "taste_beers", "text": "Провести дегустацию Boston Lager и сезонных сортов", "type": "interact", "target": "beer_tasting", "count": 2, "optional": false}, {"id": "purchase_products", "text": "Приобрести продукцию или мерч для дальнейшего использования", "type": "interact", "target": "brewery_shop", "count": 1, "optional": false}], "rewards": {"experience": 1000, "money": -30, "items": [{"item_id": "achievement_brewer_patriot", "quantity": 1}], "reputation": {}, "unlocks": ["craft_beer_quests", "brewery_access"]}, "branches": [{"branch_id": "craft_beer_enthusiast", "description": "Стать энтузиастом craft beer", "consequences": {"skills": {"charisma": 20, "culture": 15}, "reputation": {"Craft_Beer_Community": 20}, "unlocks": ["exclusive_brewery_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает на экскурсию по пивоварне Samuel Adams, связанной с именем революционера и организатора\\nБостонского чаепития. Участник знакомится с процессом варки и культурой craft beer.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до пивоварни Boston Beer Company в Jamaica Plain.\\n2. Прослушать историю бренда и связь с Сэмюэлом Адамсом.\\n3. Изучить этапы производства и оборудование.\\n4. Провести дегустацию Boston Lager и сезонных сортов.\\n5. Приобрести продукцию или мерч для дальнейшего использования.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Samuel Adams — символ бостонской гордости: имя отца-основателя, пивоварня с 1984 года и один из двигателей\\nамериканской craft beer революции. Boston Lager остаётся флагманом линейки.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 000 XP\\n- Расходы: 30 единиц валюты\\n- Бафф: +15% Charisma на 4 часа\\n- Ачивка: «Пивовар-Патриот»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест раскрывает гастрономию, образовательный компонент и связь с историческими персонами Бостона.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Относится к алкогольной линии Бостона и может пересекаться с квестами про чаепитие и иные традиционные напитки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1207, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики культурного квеста, craft beer культуры и исторического наследия."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест о пивоварне Samuel Adams в структурированный формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

