', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-philadelphia-rocky-steps", "title": "Филадельфия 2020-2029 — Ступени Рокки", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["philadelphia", "cinema", "sports"], "topics": ["culture", "lifestyle"], "related_systems": ["narrative-service", "culture-system"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/philadelphia/2020-2029/quest-004-rocky-steps.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий легендарного подъёма по ступеням Рокки был в Markdown и не участвовал в пайплайнах знаний.", "goal": "Описать культурный маршрут к Rocky Steps в YAML, подчеркнув кино-наследие и спортивный аспект.", "essence": "Игрок бежит по 72 ступеням к Philadelphia Museum of Art под мотив «Gonna Fly Now», повторяет сцену из фильма «Рокки» и\\nфотографируется у статуи героя.\\n", "key_points": [{"Расписаны действия": "подъём, поза Рокки, посещение статуи, фотосессия."}, {"Указаны детали": "72 ступени, Philadelphia Museum of Art, туристические традиции."}, "Награды дают спортивный ресурс и достижение «Рокки Бальбоа»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест объединяет кинонаследие и активный туризм: повторение сцены из фильма «Рокки» на ступенях музея искусств.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы подъёма", "body": "1. Прибыть к Philadelphia Museum of Art и подготовиться к забегу.\\n2. Пробежать по 72 ступеням под музыку «Gonna Fly Now».\\n3. На верхней площадке поднять руки в победной позе.\\n4. Сделать фотографии и записать впечатления.\\n5. Спуститься к статуе Рокки и завершить маршрут.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Уточнены количество ступеней, расположение статуи, роль сцены в поп-культуре и популярность места среди туристов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест выдаёт 600 XP, не требует затрат, добавляет +10 к спортивному ресурсу и присваивает достижение «Рокки Бальбоа».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-philadelphia-rocky-steps"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Ступени Рокки» в структуру знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

