', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-philadelphia-constitution", "title": "Филадельфия 2020-2029 — Конституция США", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["philadelphia", "constitution", "museum"], "topics": ["politics", "history"], "related_systems": ["narrative-service", "culture-system"], "related_documents": [{"id": "canon-lore-timeline-author-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/philadelphia/2020-2029/quest-005-constitution.md", "visibility": "internal", "audience": ["narrative", "vision", "education"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экскурсия по National Constitution Center была в Markdown и не участвовала в автоматизированных проверках.", "goal": "Описать квест в YAML, подчёркивая значимость Конституции и интерактивные элементы музея.", "essence": "Игрок посещает National Constitution Center, изучает оригинальные статьи, поправки и исторические дебаты, сравнивает позиции\\nфедералистов и антифедералистов и осознаёт роль документа как «живой» конституции.\\n", "key_points": ["Отмечены интерактивные экспозиции, 27 поправок и формула «We the People».", "Указан контраст исторических взглядов и современного значения.", "Награды усиливают политический ресурс и дают достижение «Конституционалист»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест показывает Национальный центр Конституции как образовательный музей, раскрывающий историю и эволюцию основного закона\\nСША.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы посещения", "body": "1. Посетить зал с оригинальным текстом и знаменитой фразой «We the People».\\n2. Изучить интерактивные экспозиции о 27 поправках и их принятии.\\n3. Принять участие в симуляции дебатов федералистов и антифедералистов.\\n4. Просмотреть панораму с фигурами подписантов и задокументировать выводы.\\n5. Сравнить американскую систему с другими конституциями и подготовить отчёт.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Уточнены год подписания (1787), статус документа как старейшей действующей конституции и роль центра в поддержке идеи\\n«живого» закона.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест выдаёт 2 500 XP, требует 15 едди на билет, добавляет +25 к политическому ресурсу и присваивает достижение\\n«Конституционалист».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-philadelphia-constitution"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "narrative_auto_converter", "changes": "Конвертация квеста «Конституция США» в структуру знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

