INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-phoenix-spring-training', 'Феникс 2020-2029 — Весенние тренировки', 'Игрок посещает весенние тренировки MLB в Аризоне, общается с игроками и получает спортивные бонусы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-phoenix-spring-training", "title": "Феникс 2020-2029 — Весенние тренировки", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["phoenix", "sports", "baseball"], "topics": ["seasonal-events", "fan-experience"], "related_systems": ["narrative-service", "event-system", "social-system"], "related_documents": [{"id": "github-issue-363", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/363", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/phoenix/2020-2029/quest-009-spring-training.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сезонный квест про MLB Spring Training находился в Markdown и не интегрировался в базу знаний.", "goal": "Структурировать событие Cactus League в YAML с описанием этапов, наград и социальной атмосферы.", "essence": "Игрок посещает весенние тренировки MLB в Аризоне, общается с игроками и получает спортивные бонусы.", "key_points": ["Квест-id PHOENIX-2029-009, сложность easy, соло-формат на 2–4 часа.", "Этапы отражают посещение матчей, получение автографов и атмосферу небольших стадионов.", "Награды дают 1 200 XP, -25 валюты, +15 к спорту и ачивку «Spring Training Фанат»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест фокусируется на бейсбольной Cactus League — командах MLB, которые проводят предсезонные тренировки в Аризоне благодаря тёплой\\nпогоде и компактным стадионам.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Выбрать матч в период февраль-март и приобрести билет.\\n2. Посетить стадион малой вместимости, наблюдая тренировочный матч.\\n3. Взаимодействовать с игроками, получить автографы и сделать фото.\\n4. Проанализировать атмосферу и возможные эффекты для спортивных сюжетов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Подчёркнуты 15 команд MLB Cactus League, мягкий климат зимы, доступность стадионов и расслабленный характер тренировок.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 1 200 XP, платит 25 едди за билеты и сувениры, а также +15 к спортивному ресурсу и титул «Spring Training Фанат».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 363, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-phoenix-spring-training"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Весенние тренировки» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

