INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-buenos-aires-2029-mate-culture', 'Буэнос-Айрес 2020-2029 — Культура мате', 'Игрок осваивает традиции сервировки, учится делиться напитком и ощущает социальную связь сообщества.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "learn_mate_preparation", "text": "Изучить процесс приготовления мате: калабас, бомбилья, температура воды", "type": "interact", "target": "mate_preparation_lesson", "count": 1, "optional": false}, {"id": "participate_circle", "text": "Принять участие в цикле передачи мате по кругу", "type": "interact", "target": "mate_sharing_circle", "count": 1, "optional": false}, {"id": "understand_social_ritual", "text": "Понять социальную связь и коллективность ритуала", "type": "interact", "target": "social_bonding", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -25, "items": [{"item_id": "achievement_mate_master", "quantity": 1}], "reputation": {"buenos_aires_locals": 10, "argentine_culture": 12}, "unlocks": ["mate_events"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-buenos-aires-2029-mate-culture", "title": "Буэнос-Айрес 2020-2029 — Культура мате", "document_type": "canon", "category": "timeline-quest", "status": "ready_for_qa", "version": "2.0.0", "last_updated": "2025-11-29T12:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["buenos-aires", "mate", "social"], "topics": ["author-events", "city-life"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/buenos-aires/2020-2029/quest-006-mate-culture.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальный ритуал мате описывался в Markdown и не имел стандартизированной записи.", "goal": "Задокументировать процесс приготовления и совместного употребления мате как игровую активность.", "essence": "Игрок осваивает традиции сервировки, учится делиться напитком и ощущает социальную связь сообщества.", "key_points": ["Мате требует набора калабаса, бомбильи и правильной температуры воды.", "Цикл предусматривает передачу напитка по кругу, подчеркивая коллективность.", "Напиток даёт энергетический бафф и вводит игрока в культурную ткань города."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "learn_mate_preparation", "text": "Изучить процесс приготовления мате: калабас, бомбилья, температура воды", "type": "interact", "target": "mate_preparation_lesson", "count": 1, "optional": false}, {"id": "participate_circle", "text": "Принять участие в цикле передачи мате по кругу", "type": "interact", "target": "mate_sharing_circle", "count": 1, "optional": false}, {"id": "understand_social_ritual", "text": "Понять социальную связь и коллективность ритуала", "type": "interact", "target": "social_bonding", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -25, "items": [{"item_id": "achievement_mate_master", "quantity": 1}], "reputation": {"buenos_aires_locals": 10, "argentine_culture": 12}, "unlocks": ["mate_events"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит с культовым напитком Аргентины. Участник приобретает набор для мате, осваивает приготовление и\\nучаствует в социальном ритуале, где напиток передаётся по кругу.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Купить калабасу, бомбилью и смесь yerba mate.\\n2. Заполнить сосуд травой и наклонить его для формирования полости.\\n3. Добавить горячую (но не кипящую) воду и установить бомбилью.\\n4. Пить мате, передавая калабасу следующему участнику.\\n5. Обсудить культурные значения ритуала и горький вкус напитка.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Калабаса традиционно изготавливается из тыквы, а бомбилья выступает фильтром. Передача мате по кругу символизирует\\nдоверие и дружбу, делая напиток важной частью повседневной жизни аргентинцев.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 800 XP\\n- Расходы: 15 единиц валюты\\n- Энергетический бафф: +15% Energy на 6 часов\\n- Ачивка: «Матеро»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Сценарий раскрывает культурную и социальную механику города, а также добавляет ресурс энергии через кофеин.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Относится к культурной линии Буэнос-Айреса и может сочетаться с заданиями про гастрономию и семейные традиции.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 325, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест по культуре мате в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

