INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-phoenix-2020-2029-desert-heat', 'Феникс 2020-2029 — «Пустынная жара»', 'Игрок переживает летний пик жары, балансируя гидратацию, кондиционирование и физические нагрузки.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-phoenix-2020-2029-desert-heat", "title": "Феникс 2020-2029 — «Пустынная жара»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T06:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["phoenix", "desert", "survival"], "topics": ["climate-extremes", "urban-hardship"], "related_systems": ["narrative-service", "weather-system", "survival-system"], "related_documents": [{"id": "canon-region-america-phoenix-2020-2093", "relation": "contextualizes"}, {"id": "github-issue-361", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/361", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/phoenix/2020-2029/quest-001-desert-heat.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик фиксировал экстремальную жару Феникса без структурированных стадий выживания.", "goal": "Описать квест о выживании в +50°C с механиками теплового удара и ресурсного менеджмента.", "essence": "Игрок переживает летний пик жары, балансируя гидратацию, кондиционирование и физические нагрузки.", "key_points": ["Квест-id PHOENIX-2029-001, тип side, сложность hard, длительность 1 день.", "Шесть стадий дневной и ночной жары, влияние на стамину и здоровье.", {"Награды": "3 000 XP, выносливость +30, ачивка «Выживший в Пустыне»."}]}, "content": {"sections": [{"id": "briefing", "title": "Брифинг и подготовка", "body": "Игрок получает предупреждение о температуре +50°C, собирает воду, охлаждающие пакеты и проверяет состояние кондиционера. Система выдаёт дебафф «Экстремальная жара».\\n", "mechanics_links": [], "assets": []}, {"id": "midday", "title": "Пик дневной жары", "body": "При выходе на улицу воздух обжигает лёгкие, асфальт плавится, металл вызывает ожоги. Игроку предлагается мини-игра по быстрому перемещению между тенью и укрытиями.\\n", "mechanics_links": [], "assets": []}, {"id": "survival", "title": "Режим выживания", "body": "Нужно удерживать гидратацию, следить за температурой тела и избегать теплового удара. Игрок решает, когда возвращаться в помещения с AC.\\n", "mechanics_links": [], "assets": []}, {"id": "night", "title": "Ночная жара", "body": "Даже ночью температура около +40°C. Игрок распределяет ресурсы, чтобы восстановить стамину и подготовиться к следующему дню.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 361, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Пустынная жара» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

