INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-buenos-aires-economic-crisis-2020-2029', 'Буэнос-Айрес: Экономический кризис', 'Квест проводит игрока через инфляцию, валютные ограничения и чёрный рынок, формируя устойчивые геймплейные триггеры.', 'main', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "research_inflation_stats", "text": "Исследование статистики инфляции, девальвации и валютного контроля", "type": "research", "target": "inflation_statistics", "count": 1, "optional": false}, {"id": "learn_blue_dollar", "text": "Знакомство с параллельным курсом «blue dollar»", "type": "investigate", "target": "blue_dollar_market", "count": 1, "optional": false}, {"id": "explore_offline_storage", "text": "Провоз доллара в оффлайн-хранилища жителей", "type": "interact", "target": "offline_currency_storage", "count": 1, "optional": false}, {"id": "monitor_price_changes", "text": "Мониторинг еженедельной смены цен и адаптация к ней", "type": "investigate", "target": "weekly_price_monitoring", "count": 1, "optional": false}, {"id": "form_crisis_conclusion", "text": "Формирование заключения о цикличности кризиса и подготовка последующих миссий", "type": "research", "target": "crisis_analysis_report", "count": 1, "optional": false}]'::jsonb, '{"experience": 4000, "currency": 0, "items": [], "reputation": {"buenos_aires_economic": 30}, "flags": ["crisis_survivor_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-buenos-aires-economic-crisis-2020-2029", "title": "Буэнос-Айрес: Экономический кризис", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["buenos-aires", "economics", "quest"], "topics": ["economy", "narrative", "quests"], "related_systems": ["narrative-service", "quest-service", "economy-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "complements"}, {"id": "github-issue-1202", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1202", "relation": "migrated_to", "migrated_at": "2025-11-23T03:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/buenos-aires/2020-2029/quest-010-economic-crisis.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В экономической ветке Буэнос-Айреса отсутствовал структурированный квест о хронических кризисах и феномене «blue dollar».", "goal": "Показать игрокам ключевые механики аргентинской экономики и их влияние на повседневную жизнь.", "essence": "Квест проводит игрока через инфляцию, валютные ограничения и чёрный рынок, формируя устойчивые геймплейные триггеры.", "key_points": ["Разобраны инфляция 50%+, обесценивание песо и контроль валюты.", "«Blue dollar» и домашнее хранение валюты превращены в игровые задачи.", "Финал закрепляет идею, что кризис — нормальная часть циклической экономики региона."]}, "quest_definition": {"quest_type": "main", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "research_inflation_stats", "text": "Исследование статистики инфляции, девальвации и валютного контроля", "type": "research", "target": "inflation_statistics", "count": 1, "optional": false}, {"id": "learn_blue_dollar", "text": "Знакомство с параллельным курсом «blue dollar»", "type": "investigate", "target": "blue_dollar_market", "count": 1, "optional": false}, {"id": "explore_offline_storage", "text": "Провоз доллара в оффлайн-хранилища жителей", "type": "interact", "target": "offline_currency_storage", "count": 1, "optional": false}, {"id": "monitor_price_changes", "text": "Мониторинг еженедельной смены цен и адаптация к ней", "type": "investigate", "target": "weekly_price_monitoring", "count": 1, "optional": false}, {"id": "form_crisis_conclusion", "text": "Формирование заключения о цикличности кризиса и подготовка последующих миссий", "type": "research", "target": "crisis_analysis_report", "count": 1, "optional": false}], "rewards": {"experience": 4000, "currency": 0, "items": [], "reputation": {"buenos_aires_economic": 30}, "flags": ["crisis_survivor_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игрок анализирует макроэкономику Аргентины и сталкивается с практиками выживания в условиях вечного кризиса.\\nСтруктура квеста делает акцент на повторяемости экономических потрясений и необходимости альтернативных каналов.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Исследование статистики инфляции, девальвации и валютного контроля.\\n2. Знакомство с параллельным курсом «blue dollar».\\n3. Провоз доллара в оффлайн-хранилища жителей.\\n4. Мониторинг еженедельной смены цен и адаптация к ней.\\n5. Формирование заключения о цикличности кризиса и подготовка последующих миссий.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Инфляция 50%+ закреплена как стандартный фон.\\n- «Blue dollar» фиксируется как неформальный инструмент обмена.\\n- Постоянные экономические циклы влияют на поведение NPC и доступность товаров.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 4 000 XP.\\n- Валютный баланс: нейтральный (0).\\n- Экономический бафф +30, открывающий уникальные сделки.\\n- Достижение «Выживший в кризисе».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Управление инфляцией и чёрным рынком.\\n- Социальные сценарии выживания, включая бартер и хранение наличности.\\n- Переключение ценовых индексов в реальном времени.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест связывает экономическую ветку Буэнос-Айреса с глобальными сценариями кризисов и подготавливает игрока к\\nвзаимодействию с финансовыми сетями НСЕР.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1202, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики управления инфляцией и чёрным рынком."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Сформирован YAML квеста «Экономический кризис» с этапами, наградами и экономическими механиками."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

