INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-chicago-willis-tower-2020-2029', 'Чикаго: Башня Уиллис', 'Игрок поднимается на Skydeck, выходит на стеклянный балкон и получает панораму мегаполиса, усиливающую чувство высоты.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "ride_to_skydeck", "text": "Подъём на Skydeck (103 этаж) с введением в историю Willis Tower", "type": "interact", "target": "willis_tower_skydeck", "count": 1, "optional": false}, {"id": "step_on_ledge", "text": "Шаг на стеклянный балкон The Ledge", "type": "interact", "target": "the_ledge", "count": 1, "optional": false}, {"id": "observe_view", "text": "Фиксация эмоций и вида на озеро Мичиган", "type": "interact", "target": "skydeck_viewpoint", "count": 1, "optional": false}, {"id": "photo_documentation", "text": "Фотофиксация и запись воспоминаний NPC/игрока", "type": "interact", "target": "photo_memories", "count": 1, "optional": false}, {"id": "complete_reflection", "text": "Заключение: возвращение с новым пониманием архитектуры города", "type": "interact", "target": "architecture_reflection", "count": 1, "optional": false}]'::jsonb, '{"experience": 1200, "money": -30, "items": [{"item_id": "achievement_chicago_heights_lord", "quantity": 1}], "reputation": {}, "unlocks": ["architecture_quests", "tourism_access"]}'::jsonb, '[{"branch_id": "architecture_enthusiast", "description": "Стать энтузиастом архитектуры", "consequences": {"skills": {"aesthetics": 20, "courage": 15}, "reputation": {"Tourism_Industry": 15}, "unlocks": ["exclusive_architecture_access"]}}]'::jsonb, '{"metadata": {"id": "canon-quest-chicago-willis-tower-2020-2029", "title": "Чикаго: Башня Уиллис", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "architecture", "quest"], "topics": ["cities", "tourism", "quests"], "related_systems": ["narrative-service", "quest-service", "world-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/chicago/2020-2029/quest-001-willis-tower.md", "visibility": "internal", "audience": ["concept", "narrative", "vision"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристическая ветка Чикаго требовала подробного описания опыта Skydeck и стеклянного балкона The Ledge.", "goal": "Зафиксировать впечатляющий вертикальный маршрут, усиливающий идентичность города небоскрёбов.", "essence": "Игрок поднимается на Skydeck, выходит на стеклянный балкон и получает панораму мегаполиса, усиливающую чувство высоты.", "key_points": ["Квест подчёркивает историческую значимость Willis Tower и переход от Sears Tower.", "The Ledge используется как ключевая проверка на смелость и туризм.", "Награды завязаны на адреналиновый бафф и городскую репутацию."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "ride_to_skydeck", "text": "Подъём на Skydeck (103 этаж) с введением в историю Willis Tower", "type": "interact", "target": "willis_tower_skydeck", "count": 1, "optional": false}, {"id": "step_on_ledge", "text": "Шаг на стеклянный балкон The Ledge", "type": "interact", "target": "the_ledge", "count": 1, "optional": false}, {"id": "observe_view", "text": "Фиксация эмоций и вида на озеро Мичиган", "type": "interact", "target": "skydeck_viewpoint", "count": 1, "optional": false}, {"id": "photo_documentation", "text": "Фотофиксация и запись воспоминаний NPC/игрока", "type": "interact", "target": "photo_memories", "count": 1, "optional": false}, {"id": "complete_reflection", "text": "Заключение: возвращение с новым пониманием архитектуры города", "type": "interact", "target": "architecture_reflection", "count": 1, "optional": false}], "rewards": {"experience": 1200, "money": -30, "items": [{"item_id": "achievement_chicago_heights_lord", "quantity": 1}], "reputation": {}, "unlocks": ["architecture_quests", "tourism_access"]}, "branches": [{"branch_id": "architecture_enthusiast", "description": "Стать энтузиастом архитектуры", "consequences": {"skills": {"aesthetics": 20, "courage": 15}, "reputation": {"Tourism_Industry": 15}, "unlocks": ["exclusive_architecture_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест ведёт игрока на смотровую площадку Чикаго, фокусируясь на эмоциях высоты и легендарном The Ledge.\\nУчастники получают информацию об истории башни и визуальные подсказки о городе.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Подъём на Skydeck (103 этаж) с введением в историю Willis Tower.\\n2. Шаг на стеклянный балкон The Ledge.\\n3. Фиксация эмоций и вида на озеро Мичиган.\\n4. Фотофиксация и запись воспоминаний NPC/игрока.\\n5. Заключение: возвращение с новым пониманием архитектуры города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Willis Tower высотой 442 метра, 110 этажей.\\n- The Ledge выступает на 1,3 метра и имеет стеклянный пол.\\n- Жители по привычке называют здание Sears Tower.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 200 XP.\\n- Стоимость посещения: -30.\\n- Адреналиновый бафф +20.\\n- Достижение «Повелитель высот Чикаго».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Туристическая механика небоскрёбов.\\n- Адреналиновые модификаторы.\\n- Фото-точки и социальные события.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест связывается с архитектурной линией Чикаго и открывает доступ к дополнительным маршрутным сценариям мегаполиса.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1183, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики архитектурного квеста, туризма и высотных видов."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Создан YAML-квест «Башня Уиллис» с этапами, наградами и архитектурным контекстом."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

