-- Quest: canon-quest-phoenix-water-crisis
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-phoenix-water-crisis', 'Феникс 2020-2029 — Водный кризис', 'Игрок исследует зависимости города от реки Колорадо, анализирует высыхание озера Мид и принимает решение о судьбе Феникса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-phoenix-water-crisis", "title": "Феникс 2020-2029 — Водный кризис", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["phoenix", "environment", "survival"], "topics": ["climate-risk", "water-management"], "related_systems": ["narrative-service", "environment-system", "decision-system"], "related_documents": [{"id": "github-issue-362", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/362", "relation": "migrated_to", "migrated_at": "2025-11-22T21:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/phoenix/2020-2029/quest-006-water-crisis.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о водном кризисе Феникса существовал только в Markdown и не проходил проверку схемы знаний.", "goal": "Перевести сценарий дефицита воды в структурированный YAML с этапами, выбором игрока и влиянием на экологию.", "essence": "Игрок исследует зависимости города от реки Колорадо, анализирует высыхание озера Мид и принимает решение о судьбе Феникса.", "key_points": ["Квест-id PHOENIX-2029-006, сложность medium, соло-прохождение на 2–4 часа.", "Этапы охватывают изучение инфраструктуры, рисков и ключевое решение «остаться, уехать или спасать город».", "Награды отражают экологическое влияние и дают ачивку «Свидетель Кризиса»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест раскрывает, как мегаполис в пустыне живёт за счёт реки Колорадо и стремительно приближается к катастрофе из-за\\nзасухи и конфликтов штатов за воду.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Проанализировать источники воды: Центральный канал Аризоны, квоты штатов, уровень озера Мид.\\n2. Изучить последствия высыхания и споры за распределение ресурса.\\n3. Исследовать инфраструктуру Феникса и уязвимости в случае отключения поставок.\\n4. Принять решение: эвакуироваться, остаться наблюдателем или инициировать спасательную программу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны 20 см осадков в год, критическая зависимость от Колорадо, падающий уровень озера Мид и полузаброшенные пригороды,\\nкоторым грозит обезвоживание.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест выдаёт 3 000 XP, прибавляет +25 к экологическому ресурсу и открывает титул «Свидетель Кризиса».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 362, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-phoenix-water-crisis"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Водный кризис» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

