INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-sf-tech-billionaire', 'Сан-Франциско — Тех-миллиардер', 'Персонаж создаёт имперскую корпорацию, проходит инвестиционные раунды и решает, как использовать накопленную власть.', 'main', 12, NULL, '{"required_quests": ["canon-quest-sf-silicon-valley-startup"], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "create_concept", "text": "Придумать концепт, собрать ядро команды и запустить прототип", "type": "interact", "target": "billion_dollar_concept", "count": 1, "optional": false}, {"id": "conduct_series_rounds", "text": "Провести Series A/B/C, управляя условиями венчурного капитала", "type": "interact", "target": "series_rounds", "count": 1, "optional": false}, {"id": "scale_to_unicorn", "text": "Перейти к оценке «единорога», расширить кампусы Palo Alto и настроить Tesla-парк", "type": "interact", "target": "unicorn_valuation", "count": 1, "optional": false}, {"id": "execute_ipo", "text": "Выполнить IPO на NASDAQ, сохранив контроль над голосами", "type": "interact", "target": "nasdaq_ipo", "count": 1, "optional": false}, {"id": "choose_final_path", "text": "Выбрать финал: филантропия, корпоративная гегемония или уход в тень", "type": "choice", "target": "billionaire_exit_choice", "count": 1, "optional": false}]'::jsonb, '{"xp": 50000, "currency": 10000000000, "items": [], "reputation": {}, "achievements": [{"id": "tech_overlord", "name": "Tech Overlord"}], "skill_boosts": {}}'::jsonb, '[{"condition": "Филантроп", "outcome": "Усиливает социальное доверие, снижает напряжение корпораций", "next_quests": []}, {"condition": "Гегемон", "outcome": "Контроль над рынком, повышает уровень конфликта", "next_quests": []}, {"condition": "Отшельник", "outcome": "Скрывает влияние в фондах, даёт уникальные clandestine-квесты", "next_quests": []}]'::jsonb, '{"metadata": {"id": "canon-quest-sf-tech-billionaire", "title": "Сан-Франциско — Тех-миллиардер", "document_type": "canon", "category": "quest", "status": "approved", "version": "2.0.0", "last_updated": "2025-11-29T15:46:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T15:46:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["san_francisco", "silicon_valley", "wealth"], "topics": ["innovation", "economy", "power"], "related_systems": ["economy-system", "entrepreneurship-system", "reputation-system", "narrative-outcome-system"], "related_documents": [{"id": "canon-quest-sf-silicon-valley-startup", "relation": "builds_on"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/san-francisco/2020-2029/quest-006-tech-billionaire.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий финального тех-восхождения существовал вне контролируемой базы и не покрывал системные последствия.", "goal": "Привести квест к стандарту знаний, указать этапы и финальные исходы для экономики и репутации.", "essence": "Персонаж создаёт имперскую корпорацию, проходит инвестиционные раунды и решает, как использовать накопленную власть.", "key_points": ["Обозначены этапы от идеи до IPO и стратегического выбора.", "Добавлены связки с economy-, entrepreneurship-, reputation-system.", "Финальные исходы меняют состояние мира и доступные ветки."]}, "quest_definition": {"quest_type": "main", "level_min": 12, "level_max": null, "requirements": {"required_quests": ["canon-quest-sf-silicon-valley-startup"], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "create_concept", "text": "Придумать концепт, собрать ядро команды и запустить прототип", "type": "interact", "target": "billion_dollar_concept", "count": 1, "optional": false}, {"id": "conduct_series_rounds", "text": "Провести Series A/B/C, управляя условиями венчурного капитала", "type": "interact", "target": "series_rounds", "count": 1, "optional": false}, {"id": "scale_to_unicorn", "text": "Перейти к оценке «единорога», расширить кампусы Palo Alto и настроить Tesla-парк", "type": "interact", "target": "unicorn_valuation", "count": 1, "optional": false}, {"id": "execute_ipo", "text": "Выполнить IPO на NASDAQ, сохранив контроль над голосами", "type": "interact", "target": "nasdaq_ipo", "count": 1, "optional": false}, {"id": "choose_final_path", "text": "Выбрать финал: филантропия, корпоративная гегемония или уход в тень", "type": "choice", "target": "billionaire_exit_choice", "count": 1, "optional": false}], "rewards": {"xp": 50000, "currency": 10000000000, "items": [], "reputation": {}, "achievements": [{"id": "tech_overlord", "name": "Tech Overlord"}], "skill_boosts": {}}, "branches": [{"condition": "Филантроп", "outcome": "Усиливает социальное доверие, снижает напряжение корпораций", "next_quests": []}, {"condition": "Гегемон", "outcome": "Контроль над рынком, повышает уровень конфликта", "next_quests": []}, {"condition": "Отшельник", "outcome": "Скрывает влияние в фондах, даёт уникальные clandestine-квесты", "next_quests": []}]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: faction. Сложность: extreme. Формат: solo. Длительность: 4+ часа.\\nНаграды: 50 000 XP, 10 000 000 000 едди, титул «Tech Overlord», глобальный модификатор технологий.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Придумать концепт, собрать ядро команды и запустить прототип.\\n2. Провести Series A/B/C, управляя условиями венчурного капитала.\\n3. Перейти к оценке «единорога», расширить кампусы Palo Alto и настроить Tesla-парк.\\n4. Выполнить IPO на NASDAQ, сохранив контроль над голосами.\\n5. Выбрать финал: филантропия, корпоративная гегемония или уход в тень.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "economy-system отслеживает капитализацию и денежные потоки, entrepreneurship-system — инновационные проверки и рост.\\nreputation-system реагирует на выбор стиля управления, narrative-outcome-system меняет политэкономическую картину мира.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны кампусы Palo Alto, гаражи Stanford, Tesla-парки и минималистичные офисы с лозунгами «move fast and break things».\\nУказаны встречи в закрытых клубах тех-элиты, медиавыступления на TED и переговоры с фондами Кремниевой долины.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Финальные решения дают 50 000 XP, 10 000 000 000 едди и титул «Tech Overlord».\\nВ зависимости от выбора доступ открывается к веткам глобального влияния, благотворительных программ или теневых консорциумов.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Исходы", "body": "- Филантроп: усиливает социальное доверие и снижает напряжение корпораций.\\n- Гегемон: даёт контроль над рынком, но повышает уровень конфликта.\\n- Отшельник: скрывает влияние в фондах и даёт уникальные clandestine-квесты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Silicon Valley Billionaire Culture", "link": "https://www.theverge.com"}, {"title": "Venture Funding Stages", "link": "https://www.cbinsights.com"}], "decisions": []}, "implementation": {"github_issue": 248, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Обновлена структура, добавлены системные крючки и финальные исходы."}, {"version": "0.1.0", "date": "2025-11-09", "author": "narrative_team", "changes": "Markdown-черновик «Тех-миллиардер»."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

